@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
  
  @font-face {
    font-family:FontAwesome;
    src:url(https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.5.0/fonts/fontawesome-webfont.ttf);
  }
  
  *::selection {
    background:#555!important;
  }
  
  *::-moz-selection {
    background:#555!important;
  }

  a,
  #yt-masthead-container #masthead-search #masthead-search-terms input,
  .branded-page-module-title-text,
  .yt-uix-subscription-button,
  #watch-related span.title,
  #watch-related span.attribution,
  .yt-uix-button-playlist-action,
  .playlist-settings-editor button,
  .playlist-add-video-button,
  .channel-header .secondary-header-contents a span,
  .add-bulletin-form button,
  .add-bulletin-form .yt-uix-form-input-select,
  .video-list-item .title,
  .video-list-item .stat.attribution,
  .about-metadata-container button,
  .about-channel-link-text,
  #yt-masthead-container a#logo-container:before,
  #yt-masthead-container #appbar-guide-button .yt-uix-button-icon:before {
    -webkit-transition:color 1s ease!important;
    -moz-transition:color 1s ease!important;
    -ms-transition:color 1s ease!important;
    -o-transition:color 1s ease!important;
    transition:color 1s ease!important;
    text-decoration:none!important;
  }





  /* Progress loader top on refresh */
  #progress,
  .ytp-volume-slider-handle::before {
    background:/*[[color]]*/!important;
  }






  /* Page background */
  #page, body > #body-container {
    background:url('http://i.imgur.com/uRwTagI.png') repeat!important;
  }







  /* Youtube top bar */
  #yt-masthead-container {
    background:-moz-linear-gradient(bottom, #222 0%, #333 100%)!important;
    background:-webkit-linear-gradient(bottom, #222 0%, #333 100%)!important;
    border:none!important;
  }

  #yt-masthead-container a#logo-container:before {
    display:table;
    font-family:FontAwesome;
    color:#fff;
    font-size:30px;
    content:"\f16a";
    text-shadow:0 0 3px #000;
  }
  
  #yt-masthead-container a#logo-container:hover:before {
    color:/*[[color]]*/;
  }
  
  .exp-responsive #yt-masthead #logo-container {
