style.css:
@-moz-document domain("nicovideo.jp") {
/* AGENT_SHEET */

/* Background Colors */
html, body, #outline, .videoExplorerContentWrapper {
background: none repeat scroll 0% 0% rgb(22, 24, 28) !important;
}

.item, li, #superBannerAds, #videoExplorerExpand, .videoExplorerBody, #videoExplorerSuperBanner, .playerContainer, .wallAlignmentArea, 
    .contentHeader, .siteHeader, .searchOption, .playerTabItem, .sortable, .panelTableHeaderContextMenu, #playerTabContainer, .cnt2, .sttl,
    .community-bbs, .textbox, .lebox > p:nth-child(6), #TS_list, #Favorite_list, .liveListInner4, #tsrNotifyDisableCheckBox, #tsrCheckMasterBox,
    .bar, #wall_canvas, #watch_player_bottom_box, #tab > li.active > h3, th, .cmn, #liveTopics, .PlaylistContainer,.PlaylistItemContent, .react-grid-Row:hover, 
    .ControllerContainer-area, .CommentInput, .content, dd, .title_index, ul.service_main, .title_light, #uneiOsusume, .link_list, .link_right, .contents_box, .title_live,
    .ticker, .ticker > *, #pager-block > a, .title_q_s, .contentDescription, .mainMenu, .HTMLInformation, .nicolib-Dialog-Premium_LeadHeader, .nicolib-Dialog-PremiumLeadContent, .nicolib-Dialog-Content, .policy-modal-wrap,
    .CommentPanelContainer [class*=___table-row___]:hover, .CommentPanelDataGrid-getScrollbarWidth::-webkit-scrollbar, .CommentPanelDataGrid [class*=___body___]::-webkit-scrollbar {
background: none repeat scroll 0% 0% rgb(40, 42, 46) !important;
border-color: rgb(55, 59, 65) !important;
color: rgb(197, 200, 198) !important;
}

#siteHeaderRightMenuContainer, .translation, .userProfile, .parentVideoInfo, .extraVideoInfo, .extraVideoTrigger, #videoReview, .relatedTagList, 
    .searchBox, .tagCaption, .tagListBox, .noticeBlock, #option_toggle_box, .sub, .formSearch, .searchFormB, .itemComment, .tab1, .clear-button-inner-search,
    .notice, .playerTabItem.active, .playerTabHeader, table, .section, .playerTabContent, .commentTableContainer, #playerSettingPanel, #playerSettingPanelContent,
     #community_prof_frm2, #commenu > ul, #tsr_my_delete_popup, .recommend_list, .mode_div_active, #form_frm_btm, #form_frm_top, .tab_0, #page_header, #page_cover,
    #watch_zapping_box, #tab_title_area, #description_tab, #community_info_tab, #netduetto_tab, #tab > li > h3, .commu_caption, .commu_info,
    .page_top, #community_news, th.active, #option_area, #search_input_block, .linksbox, .MainContainer-commentPanel, .CommentPostContainer-commentInput, .react-grid-HeaderCell,
    .VideoDescriptionExpander-switch, .SearchBox, .BottomContainer-card, .is-playing .PlaylistItemContent, .VideoMetaOverflowMenuContainer-menu, .CommentPostContainer-commandInput,
    .NGSettingPanelContainer-contentMain, .NGSettingPanelContainer-contentBottom, .NGSettingPanelContainer-contentTop, .ViewerNGList-header, .FooterContainer,
    .userDetail, .channel_open_mb8, .articleBody, .log-reslist, .toPageTopWrap, #footerWrap, .next-page-link, .exTag p, .area-pcSiteHeader, .area-communityNavigation, .area-communityHeader,
    .traceable-onair-block, .ajax-completed, .pullout a, .miniSearch > *, .footer-wrap, #dragandrophandler, .aside, .metadata, .emptyOuter, #stamp_list, .ownerMsg, #main_container, #language_setting_alertbox,
    #searchFormWrap, #timezone_offset, .itemDescription, .search_program_area, .streams_hdg, #top_temp_information_area, #top_temp_information_area > *, #box_left, #box_right, #box_right > div,
    .requestBox, .balloon, .related_pedia_outer, .related_pedia_inner, #related_nicopedia_body, .mb8p4 div, .channel_open_box, .dataOuter, .metadata > li, .login-form, .CommentPanelDataGrid-headerCell, .HeaderContainer, .BottomContainer {
background: none repeat scroll 0% 0% rgba(24, 26, 30, 1) !important;
border: 1px solid rgba(44, 46, 50, .5) !important;
}


/* Text */
label, h3, li, strong, .categoryName, .ranking, #outline, .videoExplorerMenu span, #jsFollowingAdMain, .react-grid-HeaderCell, .VideoTitle, .react-grid-Row, .BottomContainer-cardTitle, .msTypetext, #box_translation, .CommentPanelContainer .CommentPanelDataGrid { color: rgb(197, 200, 198) !important }
    .bold { font-weight: 200 !important;}

/* Faded Text */
.titles, p, .black, .value, .search_stream_description, .line, .VideoDescription-html { color: rgba(197, 200, 198, .6) !important; }


/* Inputs */
input, textarea, button, .button, .translationButton, .favVideoOwner, .videoReviewReload, .next, li > a, option, select, .playlistReplaceButton, 
    .pagerBtn, .switchingBtn, #tsrDeleteMultiple, .feedback_link > a, .community_bookmark_block, .community_history, .btnUI_yellow, #open_share a, .accountEdit, ul.stats, .btn, .buttonShape, .smooth_scroll { 
background:none repeat scroll 0% 0% rgb(40, 42, 46) !important;
border:1px solid rgb(29,31,33) !important;
color: rgb(197, 200, 198) !important;
transition: background 0.2s ease 0s;
}

input:hover, textarea:hover, button:hover, .button:hover, .formbox:hover, .div.hover, .item:hover, li > a:hover, .next:hover, .pagerBtn:hover, .switchingBtn:hover,
    #tsrDeleteMultiple:hover, .feedback_link > a:hover, .community_bookmark_block:hover, .community_history:hover, .btnUI_yellow:hover, .btn:hover, .buttonShape:hover, .smooth_scroll:hover { 
background:rgb(56,58,62)!important;transition:background .3s
}

input:focus, textarea:focus, button:focus { 
border: 1px solid rgb(129, 162, 190) !important;
}

    /* Checkboxes */
input[type="checkbox"] {
    display: inline-block !important;
    height: 12px !important;
    position: relative !important;
    vertical-align: top !important;
    width: 12px !important;
    border-radius: 2px !important;
    border: 1px solid rgb(29, 31, 33) !important;
    background: none repeat scroll 0% 0% rgb(60, 62, 66) !important;
    transition: background 0.2s ease 0s !important;
    color: rgb(197, 200, 198) !important;
    -webkit-appearance: none;
    -moz-appearance: none;
}

input[type="checkbox"]:checked:before {
    background: url("data:image/png;base64,") no-repeat scroll center -8px transparent !important;
}

    /* Radio Buttons */
input[type="radio"] {
    display: inline-block !important;
    height: 12px !important;
    position: relative !important;
    vertical-align: top !important;
    width: 12px !important;
    border-radius: 5px !important;
    border: 1px solid rgb(29, 31, 33) !important;
    background: none repeat scroll 0% 0% rgb(60, 62, 66) !important;
    transition: background 0.2s ease 0s !important;
    color: rgb(197, 200, 198) !important;
    -webkit-appearance: none;
    -moz-appearance: none;
}

    /* Dropdowns */
.chzn-single, .chzn-drop, .inner-forced, .trigger.balloon { border-color: rgb(50, 50, 50) !important; border-radius: 0px !important }
.chzn-search, .chzn-drop, .search, .trigger.balloon { background-color: rgb(55, 59, 65) !important }
.chzn-single-with-drop { box-shadow: none !important }

select { -webkit-appearance: none; -moz-appearance: none; border: 1px solid rgb(68, 68, 68) !important }


/* Colors */
a, .blue, h2, .readMore, .videoDetailOpenButton, .videoDetailCloseButton, .favVideoOwner, li a, .pagerBtn, .title_text, .VideoDescriptionExpander-switch { color:#81a2be !important }
a:hover, .red, .readMore:hover, .videoDetailOpenButton:hover, .videoDetailCloseButton:hover, .favVideoOwner:hover, li a:hover, .dataValue, th:hover, 
    .monday, .tuesday, .wednesday, .thursday, .friday, .saturday, .sunday, .Redtxt, .VideoDescriptionExpander-switch:hover, .VideoErrorMessage-title { color:#cc6666 !important }
.deepred, .new { color: #d03d3d !important }
.green, em, .active { color: #8abeb7 !important }
span[style="color: rgb(53, 44, 44);"], span[style="color: rgb(136, 48, 210);"] { color: rgb(178, 148, 187) !important }
span[style="color: rgb(181, 157, 43);"] { color: #c2c87d !important }
span[style="color: rgb(58, 102, 125);"] { color: #538eac !important }
    
/* Scrollbars */
scrollbarbutton { display: none !important; }
scrollbar, scrollcorner { -moz-appearance: none !important; width: 10px !important; height: 10px !important; }
scrollbar[orient='vertical'] { border-left: 0; }
scrollbar[orient='horizontal'] { border-top: 0; }
thumb { -moz-appearance: none !important; border: 1px solid; }
scrollbar, scrollcorner { background: #1D1F21 !important; }
thumb { border-color: #444 !important; background: #1d1f21; }
thumb:active { background: #111 !important; }
thumb:hover { background: #313131; }

body {

background-attachment: scroll ;

}
    
/* Misc Formatting */
#option_toggle_slant, #mode_bg > img:nth-child(1), .mode_active, #wall > img, .WatchAppContainer-switchToFlash, .AnywhereSeekablePremiumLinkContainer, 
    div.VideoMenuContainer-area:nth-child(2), .VideoEndScreenContainer-share, .PreVideoStartPremiumLinkContainer, .VideoStalledPremiumLinkContainer, .PremiumLink, .for_premium  
    { display:none !important }
.formSearch, .searchFormB .sbubmit input, .btnUI_yellow, #search_input_block, .SearchBox-submitButton, .SearchBox-input, .VideoMetaContainer > * { box-shadow: none !important }
span, .title_text, a { text-shadow: none !important }
.mode_div_active, #form_frm_top { border-bottom: none !important }
#form_frm_btm { border-top: none !important; }
#sort_select_area, #option_provider_area, .VideoMetaContainer > *, .react-grid-Cell, .VideoOwnerChannelLink, .VideoOwnerUploadedLink, .outer, .VideoMenuContainer:before, .CommentPanelDataGrid [class*=___table-cell___] { border-color: rgba(44, 46, 50, .5) !important; }
.result_item, #option_area { border: 1px solid rgba(44, 46, 50, .5) !important; }
#siteHeader { height:38px !important; }
.VolumeBar-progress, .SeekBar-played {background-color:#81a2be !important}
.SeekBar-buffered {background-color:rgba(197, 200, 198, .6) !important}
.ActionButton.ControllerButton, .CommentInput-textarea, .VideoStartButton, .VideoStartButton:hover, .dataOuter, .metadata > li { border:none !important}
.VideoStartButton, .VideoStartButton:hover { background: none !important;}
.VideoMenuButton > svg, .CommentPanelMenuContainer-mainMenuArea > .ActionButton > svg, .VideoEndScreenContainer-videoMenu > .ActionButton > svg { fill: rgba(197, 200, 198, .6) !important; }
.VideoMenuButton:hover > svg, .CommentPanelMenuContainer-mainMenuArea > .ActionButton:hover > svg, .VideoEndScreenContainer-videoMenu > .ActionButton:hover > svg  { fill: #81a2be !important; }
.VideoDescriptionExpander-switchExpand:before {border-top-color: #81a2be !important;}
.VideoDescriptionExpander-switchCollapse:before {border-bottom-color: #81a2be !important;}

}
