style.css:
@-moz-document regexp('https://msdn.microsoft.com/..-../library/.*') {

    /* Background color for everything except footer */
    body {
        color: #dcdcdc;
        background-color: #1e1e1e;
    }

    /* Normal text */
    p {
        color: #dcdcdc;
    }

    /* Main header */
    h1, h1.title, .topic h1.title {
        color: #ffffff;
    }

    /* Collapsible headers */
    h1.heading, h2, h2.heading, h2.subheading, h3, h3.subheading, h4, h4.subheading, h5, h5.subheading, .LW_CollapsibleArea_Title {
        color: #ffffff
    }

    /* Triangles */
    .cl_CollapsibleArea_expanding {
        background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAMCAIAAAA/PgD0AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuM4zml1AAAAA9SURBVChTrclJEQAgDANAlGCizqKnXksYGM7wY7+b8tv3MzN97g5AHCMixLUQN4K2W4PmHUH97qB6MghAAclug7wgVoWFAAAAAElFTkSuQmCC') no-repeat scroll;
    }

    .cl_CollapsibleArea_collapsing {
        background: url('data:image/png;base64,') no-repeat scroll;
    }
    /* End: Collapsible headers */

    /* Version of .NET framework under the main header */
    #curversion {
        color: #6e6e6e;
    }

    /* Links */
    .topic a, .topic a:link {
        color: #569CD6
    }

    /* Navigation links */
    div#leftNav a, div#leftNav a:link, div#leftNav a:visited {
        color: #569CD6
    }

    /* Expanded navigation section */
    .toc_expanded {
        background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAARCAIAAAC0D9CtAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuM4zml1AAAABGSURBVDhPY5AjHYwwPRoaGmZmZlAODODTA9Rw6tSpsLAwKB8GcOqBaPj//z+xeuAaiNWDrAEICOtB0wAEJPgHDxjVIycHADyYjlHnj01oAAAAAElFTkSuQmCC') no-repeat scroll
    }

    /* Collapsed navigation section */
    .toc_collapsed {
        background: url('data:image/png;base64,') no-repeat scroll
    }

    /* Current navigation section */
    div#tocnav > div.current > a, div#tocnav > div.current > a:link, div#tocnav > div.current > a:hover, div#tocnav > div.current > a:focus, div#tocnav > div.current > a:visited {
        color: #ffffff;
    }

    /* Description of members of a class */
    td {
        color: #dcdcdc;
    }

    /* Ordinary code color */
    [style*="Black"] {
        color: #dcdcdc !important;
    }
    
    /* Keywords */
    [style*="Blue"] {
        color: #569cd6 !important;
    }
    
    /* Comments */
    [style*="Green"] {
        color: #57a64a !important;
    }
    
    /* String literals */
    [style*="A31515"] {
        color: #d69d85 !important;
    }

    /* Inline code */
    /* The color of user types in VS2013 */
    .code {
        color: #4ec9b0 !important;
    }
}
