style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("openrepos.net") {
  #page, #main-wrapper, #header, body {
      background: #151515;
      color: #bbb;
  }
  
  .application-tiles td {
    background-color: #1c1c1c;
    border-color: #202020;
  }
  
  .application-tiles td:hover {
    background-color: #2c2c2c;
    border-color: #303030;
  }
  
  #main-menu-links a, #main-menu-links a:focus {
    background-image: none;
    background-color: #202020;
    color: #eee;
    text-decoration: none;
  }
 
  #page-title {
    color: #eee;
  }
 
  .sidebar .block {
    background-color: #252525;
  }
  
  a {
    color: #90a959;
  }
  
  a:hover {
    color: #c0ac5c;
  }
  
  
  .node-teaser h2 a {
    color: #90a959;
    font-weight: 500;
  }
}
