@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.juegosparawindows.com") {

body
{
  background:none repeat scroll 0% 0% #181818;
  color:#5B5B5B;
}
  
#main-content
{
  background:none repeat scroll 0% 0% #222222;
}
.cat-box-content, #sidebar .widget-container, .post-listing
{
  background:none repeat scroll 0% 0% #161616;
  box-shadow:none!important;
  border-color:#404040 #404040 #F88C00;
}

a {
    color: #46a5d4!important;
}
a:hover,p.post-meta a:hover,.commentmetadata a:hover
{
  color:#00DBC9;
}
.item-list  
{
  border-bottom:1px solid #404040;
}
*
{
  color:#585858;
  text-shadow:none!important;
  box-shadow:none!important;
}
.page-title
{
  color:#FFCE3E;
}
.post-inner p.post-meta
{
  border-bottom:1px solid #FFCE3E;
}

.comment-wrap
{
  background:none repeat scroll 0% 0% #181818;
}
.msg-fg
{
  color:#585858;
}
#IWW input
{
  color:#F0F0F0;
}
input.ubdr
{
  border:1px solid #404040!important;
}

h2.post-box-title a {
    color: #365899!important;
}
#wrapper.boxed-all {

    background: #161616;
}
#crumbs, .woocommerce-breadcrumb {
    margin-top: 10px;
    border-bottom: 1px solid #ef3636;
}
.msg-fg p {
    color: #6f6f6f;
}
fieldset {
    border: 2px solid #2d2d2d;
    background: rgba(0, 0, 0, 0);
}
font[style="color:black"],
font[style*="black"] 
{ color: red  !important;}
span[style="color:#000"],
span[style*="#000000"] 
{ color: gray !important; }

span[style="color: rgba(0, 0, 0)"],
span[style*="rgb(0, 0, 0)"] 
{ color: gray !important;}
#tabbed-widget .tabs-wrap li, .comments-avatar .widget-container li, .posts-list .widget-container li, .categort-posts .widget-container li, .authors-posts .widget-container li, .widget.woocommerce .widget-container li, .widget-container ul.reviews-posts li, .widget_categories .widget-container li, .widget_archive .widget-container li, .widget_nav_menu .widget-container li, .widget_meta .widget-container li, .widget_pages .widget-container li, .widget_recent_comments .widget-container li, .widget_recent_entries .widget-container li, .widget_display_forums .widget-container li, .widget_display_views .widget-container li, .widget_display_replies .widget-container li, .widget_display_topics .widget-container
li
{
border-bottom: 1px solid #272727;
}
.c_username {
    color: #2a6780!important;
}
.msg-fg p {
    color: #596373 !important;
}
.msg {
    border-bottom: 1px solid #1d2129!important;
}

}
