style.css:
@-moz-document domain("8ch.net"), domain("lainchan.org"), domain("8chan.co") {
body {
    background: url("http://i.imgur.com/sVZRDXF.png") repeat rgb(0, 0, 0);
    font-family: /*[[font]]*/  !important;
}

body {color: rgb(205,205,205);}
div.post.reply {
    background: none repeat scroll 0% 0% rgba(33, 33, 33,0.5);
    border-width: 0px;
    border-style: none solid solid none;
    border-color: rgb(183, 197, 217);
    display: inline-block;
    max-width: 94% !important;
    font-family: /*[[font]]*/ ;
}
  div.post.reply:hover {
    background: rgba(33, 33, 33,1)
}
  div.post.reply.post-hover {
    background: rgba(33, 33, 33,1);
}
    div.post.reply.highlighted {
      background: rgb(77,77,77)
    }
  a, a:visited,p.intro a.email span.name {
    text-decoration: underline;
    color: rgba(255, 181, 5, 1) !important;
}
div.boardlist {background-color: rgb(33,33,33) !important;}
  p.intro span.name {
    color: #117743;
    font-weight: bold;
    font-family: /*[[font]]*/ ;
}
  span.quote {color: #789922;}
  p.intro span.subject {color: rgba(43, 201, 95, 1);}
.options_tab, #options_tablist {
    background-color: #212121;
}
      div.post.reply div.body a {
    color: #D00;
}
    div.boardlist {
    color: #89A !important;
    font-size: 9pt;
    font-family: /*[[font]]*/
}
    .content_menu,.button {
    background-color: #212121;
    font-family: /*[[font]]*/  !important;
}
    header {
    background: none repeat scroll;
    border: 0px !important;
}
    table.modlog tr th, table.modlog tr td {
    background: none repeat scroll 0% 0% #212121;
}
    table tbody tr td {
    background: none repeat scroll 0% 0% #424242;
}
    .theme-catalog div.thread:hover {
    background: none repeat scroll 0% 0% #212121;
    border-color: #B7C5D9;
    font-family: /*[[font]]*/ ;
}
    section.description.box.col.col-12 h2{
    background: none repeat scroll 0% 0% #212121;
    color: rgba(255, 181, 5, 1) !important;
    }
    section.description.box.col.col-12 {
    background: none repeat scroll 0% 0% #424242;
    color: rgb(205,205,205);
    }
    form#search-form.box {
    background: none repeat scroll 0% 0% #424242;
    }
    form#search-form.box {
    color: rgb(205,205,205);
    }
    form#search-form.box h2.box-title {
    background: none repeat scroll 0% 0% #212121;
    color: rgba(255, 181, 5, 1) !important;
    }
    table.board-list-table .board-tags .board-cell:hover {
    background: #424242 !important;
    }
    table.board-list-table tr:nth-of-type( even ) .board-tags .board-cell {
    background: #424242
    }
    th.board-meta,th.board-uri,th.board-title,th.board-pph,th.board-max,th.board-unique,th.board-tags{
    background: none repeat scroll 0% 0% #212121;
    color: rgba(255, 181, 5, 1) !important;
    }
div.pages {
color: #CDC6BA;
background: #212121;
display: inline;
padding: 8px;
border-right: 1px solid #212121;
border-bottom: 1px solid #212121;
}
form table tr th {
background: #101010;
}
style div.boardlist:nth-child(1) {
border-bottom: 0px solid;
}
div.post.op {
margin-right: 20px;
margin-bottom: 5px;
background-color: rgba(33,33,33,0.5);
}
header div.subtitle,h1 {
  color: #AF0A0F;
  text-align: center;
  font-family: /*[[font]]*/ ;
 }
}
