style.css:
@-moz-document domain("news.qq.com") {
/*头条*/
h2 { font-family: "文泉驿微米黑" !important; }

/*列表*/
#news .Q-tpList em,
#news .Q-pList em a { font-family: "文泉驿微米黑" !important; }

/*国内、国际等条幅高度*/
#newsBanner { height:auto !important; }

/*禁用右栏*/
.side { display: none !important; }

/*拉伸左栏*/
.main { width: 100% !important; }

/*正文标题*/
#C-Main-Article-QQ h1 { font-family: "文泉驿微米黑" !important; }

/*正文*/
#Cnt-Main-Article-QQ p {
  margin:20px ;
  font-size: 26px !important;
  font-family: "文泉驿微米黑" !important;
  line-height: 1.5em !important;
  color: #3c3c3c !important;
}


.ft,              /*相关阅读，热门推荐*/
.foot-Article-QQ  /*页脚*/
  { display: none !important;  }
}
