style.css:
@-moz-document regexp("^http(s)?://(www\\.)?purepc\\.pl.*") {
html, body, #top, .b_node, .cookiesinfo, .cip-ui-widget-content{
  background-image: none !important;
  background-size: 0 !important;
  background: #000 !important;
}

body {
    display: unset;
}

*, .cookiesinfo{
    color: #ddd;
}

.fotopromo_head, .fotopromo, .promo, .lastten_grp{
    display: none !important;
}

.container .latest{
    margin: 0;
}

div[style*="background:yellow"]{
   background: rgb(166, 30, 30) !important;
}

/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- 
/* ====            TOPKA             === 
/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- */
.header{
    background: none;
}

/* panel logowania */
.loginbox a, .lb_logged, .lb_logged *, .loginbox input{
    color: #ddd;
}

.loginbox input.login, .loginbox input.password{
    color: #ddd;
    background: #333;
}

/* przyciski społecznościowe w menu */
.h, .s_facebook, .s_twitter, .s_gplus, .s_youtube, .s_instagram, .s_rss {
    opacity: 0.8;
}

/* kolor odnośników w menu */
.menu ul li a, .menu ul li span, .promonav a{
    color: #ddd;
}

ul.pager li a, ul.pager li{
    color: #ddd;
    background: #000;
}

ul.pager li:hover a{
    color: #c33;
    background: #000;
}

/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- 
/* ====         SZUKAJKA             === 
/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- */
.searchbox input[type=text]{
    background: #000;
    color: #ddd;
}

.searchbox input[type=submit]{
    background-color:#A61E1E;
    opacity: 1;
}

.searchbox input[type=submit]:hover{
    background-color:#C82626;
    opacity: 1;
}

/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- 
/* ====          GŁÓWNA              === 
/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- */

/* tło kontenera */
.container, .latest_items, html > body{
    background-color: #000 !important;
}

.nl_item{
    background-color: #000;
    border: 1px solid #333;
}

/* tło kafelków z artami */
.ln_item{
	background-color: #1a1a1a;
    border: 1px #000 solid;
    color: #ddd;
}    

.ln_item .ni_meta, .nl_item .ni_meta{
    background-color: #000;
    border: 1px #aaa solid;
    border-width: 1px 0;
}

.pb_meta h2,.ln_item p, .ln_item .ni_meta, .ln_item .ni_meta *, .nl_item h2, .nl_item p, .nl_item .ni_meta{
	color: #ddd; 
}

.pb_meta h2 a, .ps_meta h2 a{
    text-shadow: #000 0px 0px 1px,   #000 0px 0px 1px,   #000 0px 0px 1px,
                 #000 0px 0px 1px,   #000 0px 0px 1px,   #000 0px 0px 1px;
    color: #ddd;
}

/* lista artów */
.lt_list{
    background: #161616;
}


/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- 
/* ====            ARTY              === 
/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- */
.artbox, .node .ni_meta{ /* tło */
    background: #161616;
}

.node .ni_meta, .nl_item .ni_meta *{
    color: #bbb;
}

.sb_head, .breadcrumbs *, .node p, .content p span[style]{ /* kolor nawigacji i treści */
    color: #ddd !important;
}

/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- 
/* ====           BOCZNY             === 
/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- */
.sb_list{
    background: #000;
    border: 1px solid #1a1a1a;
    padding: 0;
}

.sb_list .sbl_foto{
    border: 1px solid #1a1a1a;
    margin: 0 0 2px;
    width: inherit;
    height: 80px;
}

.sb_list .sbl_foto h2{
    text-shadow: #000 0px 0px 1px,   #000 0px 0px 1px,   #000 0px 0px 1px,
                 #000 0px 0px 1px,   #000 0px 0px 1px,   #000 0px 0px 1px;
    color: #ddd;
}

.sb_head{
    font-weight: 600;
	background: #AD0C0C;
}

p.cyt, h3.cyt, p.cyt_m, h3.cyt_m{
	background:url(http://i.imgur.com/ZppKOzt.jpg);
    background-repeat: no-repeat;
    color: #c33;
}

p.recenzja, h3.recenzja{
	background:url(http://i.imgur.com/IxU3Ye3.jpg);
    background-repeat: no-repeat;
    color: #c33;
}

.similar_nodes, .node .ni_meta.tags, .nl_item .ni_meta.tags{
    background: none;
}

/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- 
/* ====         KOMENTARZE           === 
/* -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=- */
.comment:hover {
  background: #000;
  opacity: 1;
}
.comment .links{
    background: none;
}

.comment blockquote{
    background:#090909 url(http://i.imgur.com/ZppKOzt.jpg);
    background-repeat: no-repeat;
    border: 1px #222 solid;
    padding: 6px 6px 6px 32px;
}

.comment blockquote blockquote{
	background: #121212;
}

select{
    background: #000;
    color: #ddd;
}

input[type=text], input[type=password], textarea{
 	background: #000;
    color: #ddd;
}


.authorx img{
    opacity: 0.7;
}

.node .content table.zestaw td:first-child, .node .content table.platforma td:first-child, .wykres{
    background: none;
}

.wykres *{
    color: #ddd !important;
}

.node .content table.zestaw tr:first-child td, .node .content table.platforma tr:first-child td{
    background: #333 !important;
}

.artbox .node .content .PageMenuList ul li a{
    background: #000;
    color: #ddd;
}

table.specy tr:nth-child(even) td {
    background: #000000;
}

table.specy tr:nth-child(odd) td {
    background: #1f1f1f;
}

table.specy tr:first-child td {
    background: #AD0C0C;
}


}
