style.css:
@-moz-document domain("spacebattles.com"){
.userBanner.bannerOlive.wrapped strong,.userBanner.bannerOlive strong {
	display: none;
}
.userBanner.bannerOlive.wrapped:after,.userBanner.bannerOlive:after {
	content: "/*[[btitle]]*/";
}

.userBanner {
    background-image: none !important;
}

.userBanner.bannerOlive {
	color: /*[[tcolor]]*/;
	background-color: /*[[bcolor]]*/;
	border-color: /*[[ocolor]]*/!important;
}

.userBanner.bannerOlive.wrapped span {
	background-color: /*[[ocolor]]*/!important;
}

.messageUserBlock .userBanner {
	margin-top: 4px;
}
}
