style.css:
@-moz-document domain("torrentz.eu") {
iframe,
.SimpleAcceptebleTextAds,
div.download > dl:first-of-type
{
    display: none;
}

div.votebox
{
	height: 190px;
	overflow: hidden;
}
}

@-moz-document regexp("^https?\\:\\/\\/torrentz\\.eu\\/\\w{40}$") {
form.search {
	margin: 20px 0 225px;
}


.download + div {
	position: absolute;
	top: 145px;
	width: 1400px !important;
}


div.trackers {
	margin: 0 10% 0 0;
	float: left;
	width: 45%;
	height: 200px;
	overflow: hidden;
}


span.replist:nth-of-type(3) {
	display: none !important;
}
}
