style.css:
@-moz-document domain("twitter.com") {
/* Hides sidebars and stuff */
.dashboard-right,
.dashboard-left,
.bird-topbar-etched,
.topics,
.ProfileSidebar--withRightAlignment
{
  display: none!important;
}

/* Fills up that nice space with the tweets you read */
#timeline
{
  width: 80%;
  margin: 0 10% 0;
}

/* Centers images in tweets */
.is-preview>img
{
  margin: auto;
}

/* Sets the rounded corners of the search box to be the same as other stuff */
#search-query
{
  border-radius: 5px;
}
}

@-moz-document url("https://twitter.com/") {
#timeline
{
  margin-top: 49px;
}

.timeline-tweet-box
{
  position: fixed;
  top: 46px;
  left: 0px;
  width: 100%;
  border-radius: 0!important;
  z-index: 100;
}

.timeline-tweet-box>div:first-child
{
  border-radius: 0!important;
}

.global-nav,
.global-nav-inner,
.topbar
{
  border-bottom: none;
}
}
