style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("criticallytouched.com") {
  
  body, table, .leftcolumn, .rightcolumn, .main-headercolor, .splash, .t_con { background: #fff !important; color: #666 !important; }
  
  .headertext, .subheader-text { color: #000 !important;}
  
  .subheader-text span, .score-divider, .score-divider span {color: #666 !important;}
  
  
  a { color: chocolate !important; }
  a:hover { text-decoration: underline !important; }
  
  .commentbody, .comment-header-text, .comment-date, .comment-text { background: #eee !important; color: #666 !important;}
  
  
  table, tr, td { color: #666 !important;}  
  tr.bgcolor1 { background-color: #eee !important;}
  tr.bgcolor2 { background-color: #fefefe !important;}
}
