style.css:
@-moz-document domain("shikimori.org") {
/*Night theme*/
html
{  
  background-color: rgb(0,0,0)!important;
}
body 
{ 
  background: url("/*[[bgImage]]*/") repeat !important;   
}

.l-page, h1,h2, blockquote, div.tooltip-inner, div.sticked-tooltip, .pagination, .notice, .score-notice, .title, .close, .headline, .b-quote, .c-my_list, .expanded-options .option, .ac_even, .b-link_button, form, .summary.lines.list
{  
  background-color: rgb(20,20,20)!important;
  color: rgb(120,120,120)!important;
}

h2, .headline, .bar.vertical .line, article, .c-my_list, .expanded-options .option, .ac_results li, form, .reload
{  
  border:none!important;
}

input, .input, textarea, .ac_odd
{  
  background-color: rgb(50,50,50)!important;
  color: rgb(120,120,120)!important;
}

div.collapsed, .selectable:hover, .block-list li:hover, .block-list li.selected, .comments-shower, .comments-hider, .click-loader, .footer-vote, .ac_results
{  
  background-color: rgb(50,50,50)!important;
  color: rgb(120,120,120)!important;
  border:none!important;
}
div.subheadline, div.subheadline a, h2.subheadline, h2.subheadline a, h2.midheadline, h2.midheadline a, div.midheadline, div.midheadline a, .trigger, .b-link_button:hover{  
  background-color: rgb(50,50,50)!important;
  color: rgb(29,120,183)!important;
  border:none!important;
}
div.collapsed, tr.border, .tooltip-arrow, .block-list input
{  
  display:none!important;
}

.b-options-floated .selected.always-active, .sitemap, span.stat-value, .tag, .b-tag, .b-entry-info .value, .cover .misc span, .total-rates, a.selected, h2 a, .b-options-floated .selected, .video-author, .episode-num, .stat_name a, .name, a .title, .reload{
  color: rgb(29,120,183)!important;
}

.voteable, .voteable .vote{
 color: rgb(120,120,120)!important;
}   
.voteable .vote.selected, .voteable .vote:hover{
 color: rgb(29,120,183)!important;
} 
.episodes_total, .current {
color: rgb(255,133,27)!important;
}
.tag:hover, .b-tag:hover{
  color: rgb(255,255,255)!important;
}   

.l-top_menu, .to-top-fix{
  background:none!important;
}
.shade{
  background-image:linear-gradient(to bottom, rgba(255,255,255,0), #303030) !important;
  height:30px !important;;
  margin-top:-30px !important;
}
.b-comment, .b-offtopic_marker:before
{
background-color: transparent !important;
}

.b-height_shortener .expand {
	opacity: 1;
}
    
.ajax-loading{
background:url('data:image/gif;base64,') no-repeat center;
  }
}
