style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("humblebundle.com") {

  ul.platforms .hb-steam, ul.platforms .hb-android, ul.platforms .hb-drmfree { float: left; margin-top: 2px; width: 24px; height: 20px; }
  ul.platforms .platform-exception { display: none; }
  ul.platforms .platform-info { z-index: inherit;  font-size: 13px; line-height: 13px; padding: 4px 5px; width: 72px; position: relative; top: 0; left: 0; opacity: 1; margin: 0; background-color:#DDD; margin-left: 26px; bottom:0 }
  ul.platforms  li { width: 100%; }
  ul.platforms  li > i { cursor:initial; opacity: 1 }
  ul.platforms  li + li { margin-left: 0 ; padding: 0; margin-top: 1px }
  .storefront-main aside.platform-info { display: block }
  ul.platforms .platform-info p { margin: 0; overflow: hidden; width: 18px; display: inline-block;  height: 15px }
  ul.platforms i ~ .platform-info { opacity:1 }
  ul.platforms .platform-info p i { width: 18px; height: 15px } 
  ul.platforms .platform-unavailable { opacity: 0.4 }
  ul.platforms .platform-info::after { display: none } 
}
