style.css:
@-moz-document domain('bash.im') {

body {
    color: #a2a2a2 !important;
    background: #242424 !important;
}

div#footer a,
.submenu div.options a,
.submenu div.options a.add {
    border-color: gray !important;
}

div#footer a:hover,
.submenu div.options a:hover,
.submenu div.options a:hover.add {
    border-color: #B2B2B2 !important;
}

input[type="text"], textarea {
    color: gray !important;
    background: #333333 !important;
    border: 1px dotted gray !important;
    border-radius: 3px !important;
}
    
div#search button, a.button
{
    text-shadow: none !important;
    color: gray !important;
    background: #202020 !important;
    border: 1px dotted gray !important;
    border-radius: 3px !important;
}

a:link, a:visited, a.old, a.b-share__handle span,
span.share {
    color: gray !important;
    overflow: visible;
}

div.actions a:hover,
a:hover, a:hover.old, a:hover.b-share__handle span {
    color: #B2B2B2 !important;
    background: #333333 !important;
}

div#xsite-nav {
    background: #202020 !important;
}
    
div#xsite-nav a.current, div#xsite-nav a:hover,
.submenu div.options a.current, .submenu div.options span.current {
    background: #333333 !important;
}

em.hot {
    background: #333333 !important;
}

div.text {
    background-color: #212121 !important;
    border: 1px dotted gray !important;
    border-radius: 3px !important;
}

}
