style.css:
/* Ylilauta Anonymous Theme v1.5 */

@-moz-document domain("ylilauta.org") {
::selection {
    background: #006800 !important;
    color: black;
}
::-moz-selection {
    background: #006800 !important;
    color: black;
}
body {
    background-color: #000000 !important;
    background:  url('http://static.hdw.eweb4.com/media/wallpapers_1920x1200/meme/1/1/anonymous-meme-hd-wallpaper-1920x1200-371.jpg') right center fixed !important;
    -webkit-background-size: cover;
    background-repeat: no-repeat !important;
    background-size: cover !important;
    color: #00bb00 !important;
    font-weight: bold;
    background-position: center !important;
}
a {
    color: #00bb00 !important;
}
a:hover {
    text-decoration: underline !important;
}
nav {
    border-color: #005600 !important;
}
nav a {
    background: transparent !important;
    padding: 2px !important;
}
nav a:hover {
    text-decoration: none !important;
    background: rgba(0, 35, 0, .7) !important;
}
input, textarea, select {
    border: 1px solid #00bb00 !important;
    background-color: black !important;
    color: #008800 !important;
}
input[type="password"] {
    margin: 2px 0 2px !important;
}
button, .button, .linkbutton {
    border: none !important;
    background-color: rgb(0, 35, 0) !important;
    color: rgb(0, 136, 0) !important;
    text-decoration: none !important;
    border-radius: 2px !important;
}
button:hover, .button:hover, .linkbutton:hover {
    text-decoration: none !important;
    color: #00bb00 !important;
    background-color: rgb(0, 28, 0) !important;
}
hr.clear {
    visibility: hidden !important;
}
li.header {
    background-color: #002300 !important;
    color: #00bb00 !important;
}
li a:hover {
    background-color: #003c00 !important;
}
h3.center {
    background-color: #002300 !important;
    color: #00bb00 !important;
}
span.pages span.cur {
    background-color: rgba(0, 0, 0, .5) !important;
}
p.thread-boardinfo {
    border-bottom-color: #005600 !important;
    background-color: #001300 !important;
}
body p[style="text-align: right"] {
    background-color: rgba(0, 0, 0, .5) !important;
    margin: 0px !important;
    padding: 10px 1px 10px 0px;
}
.dialog {
    background-color: rgba(0, 0, 0, .9) !important;
    border: 1px solid #00bb00 !important;
}
.boardheader {
    border-bottom: 2px solid #005600 !important;
}
.boardheader h1 {
    font-weight: 400 !important;
}
.boardheader h1 a:hover {
    text-decoration: none !important;
    text-shadow: -1px -1px 0 rgb(0, 80, 0), 1px -1px 0 rgb(0, 80, 0), -1px 1px 0 rgb(0, 80, 0), 1px 1px 0 rgb(0, 80, 0);
}
.navigationbar {
    border: 1px solid #005600 !important;
    margin-bottom: 20px;
    background-color: #002300 !important;
    border-color: #005600 !important;
    padding: 0px 15px 0px 15px !important;
}
.navigationbar a {
    color: #008800 !important;
}
.navigationbar a:hover {
    background-color: rgb(0, 35, 0) !important;
    color: #00bb00 !important;
    text-decoration: none !important;
}
.label {
    background-color: #002300 !important;
    border-color: #00bb00 !important;
}
.pagination {
    background-color: transparent !important;
    border: none !important;
}
.thread {
    background-color: rgba(0, 0, 0, .5) !important;
    border: 1px solid #005600 !important;
    font-weight: normal;
}
.thread {
    padding-bottom: 10px !important;
}
.style-box .thread .message {
	background-color: rgb(0, 0, 0, .8) !important;
}
.style-box .thread .message .post-file.thumb {
	background-color: transparent !important;
}
.style-box .thread .omitted {
	background-color: rgb(0, 35, 0) !important;
    box-shadow: 0 0 10px 10px rgb(0, 35, 0) !important;
    color: #008800 !important;
}
.style-compact {
	background-color: rgb(0, 0, 0, .5) !important;
}
.style-compact .thread {
	padding: 0 !important;
    background-color: rgb(0, 35, 0, .7) !important;
}
.style-compact .thread:nth-of-type(2n) {
	background-color: rgba(0, 0, 0, .7) !important;
    border-width: 0 1px !important;
}
.style-compact .thread .post.op_post {
	background-color: transparent !important;
}
.style-compact .thread .post.op_post .postsubject {
	background-color: transparent !important;
}
.style-compact .thread .post.op_post .postinfo {
	box-shadow: none !important;
	background-color: transparent !important;
}
.style-compact .thread .omitted {
	background-color: transparent !important;
    color: #008800 !important;
    margin: 0 !important;
    padding: 0 10px 3px !important;
}
.threadcontent {
    background-color: rgba(0, 0, 0, .7) !important;
    border: 1px solid #005600 !important;
}
.postsubject {
    background-color: #002300 !important;
    font-weight: 400;
}
.postsubject span {
    background: transparent !important;
}
.postsubject:hover {
    text-decoration: none !important;
}
.postsubject .subject:hover {
    color: #008000 !important;
}
.postinfo {
    box-shadow: 0px 5px 5px -5px #005600 !important;
    border-bottom-color: #005600 !important;
    background-color: rgba(0, 35, 0, .7) !important;
}
.box {
    background-color: rgba(0, 0, 0, .5) !important;
    border-color: #00bb00 !important;
}
.box h3 {
    background-color: #002300 !important;
    color: #00bb00 !important;
}
.boxes h3 {
    background-color: #002300 !important;
    color: #00bb00 !important;
}
.rowbg {
    background-color: rgba(0, 35, 0, .5) !important;
}
.asyncreply {
    background-color: rgba(0, 20, 0, .7) !important;
    border: 1px solid #00ff00 !important;
}
.news h3 {
    background-color: #002300 !important;
    color: #00bb00 !important;
}
.newstext hr {
    border: 0px !important;
    border-top: 1px solid #005600 !important;
    border-bottom: 1px solid #005600 !important;
}
.newstext img {
    margin-left: 20px !important;
    -webkit-filter: invert(1) hue-rotate(310deg) !important;
    filter: invert(1) hue-rotate(310deg) !important;
}
.answer {
    background-color: rgba(0, 35, 0, .2) !important;
    border-color: #005600 !important;
    margin-bottom: 10px !important;
}
.highlighted {
    background-color: rgba(9, 255, 8, .2) !important;
    border-color: #005600 !important;
    margin-bottom: 10px !important;
}
.replies {
    border-color: #005600 !important;
}
.tooltip {
    background-color: rgba(0, 0, 0, .9) !important;
    border: 1px solid #00bb00 !important;
    font-weight: normal;
}
.spoiler {
    background: none repeat scroll 0% 0% #005600 !important;
    color: #005600 !important;
}
.spoiler a {
    color: #005600 !important;
}
.spoiler:hover {
    background: none repeat scroll 0% 0% #000 !important;
    color: #00bb00 !important;
    border: 1px solid #005600 !important;
}
.spoiler:hover a {
    color: #00bb00 !important;
}
.infobar {
    display: none !important;
}
.tlist_thread {
    background-color: rgba(0, 0, 0, .5) !important;
    border: 2px solid #005600 !important;
    padding-bottom: 5px !important;
}
.tlist_thread .image {
    background-color: rgba(0, 0, 0, .1) !important;
}
.tlist_thread .postsubject {
    border-bottom-color: #005600 !important;
    font-weight: bold !important;
}
.answer a.linkbutton img {
    -webkit-filter: invert(1) hue-rotate(310deg) !important;
    filter: invert(1) hue-rotate(310deg) !important;
}
.notification {
    border-bottom: 1px solid #005600 !important;
}
.notification.unread {
    background-color: #002300 !important;
}
.goldhide-info {
    border-color: #005600 !important;
}
.post.truncated::after {
    background: linear-gradient(to bottom, transparent 0%, rgb(0, 35, 0) 100%) !important;
}
.banlist table {
    border: 1px solid #005600 !important;
    margin-bottom: 20px !important;
}
.banlist table thead {
    background: rgb(0, 86, 0) !important;
    color: #00bb00 !important;
}
.banlist table tr {
    background: rgba(0, 0, 0, .7) !important;
}
.banlist table tbody tr:hover {
    background: rgba(0, 35, 0, .7) !important;
}
.banlist table th {
    border-color: #005600 !important;
}
.banlist table td {
    background: none !important;
    border-color: #005600 !important;
}
.postuids {
    background: #003000 !important;
    padding: 0 4px 1px !important;
    margin-top: 1px !important;
}
.messageoptions a {
    background: #003000 !important;
}
.messageoptions a:hover {
    background: #005600 !important;
}
.goldhide-info a {
    font-style: italic !important;
    color: #005600 !important;
}
.messageoptions_mobile.icon-menu {
    background-color: #003000 !important;
}
.linkbutton {
    background-color: rgb(0, 20, 0) !important;
    border: 1px solid #005600 !important;
    color: #009800 !important;
}
.linkbutton:hover {
    border: 1px solid #008600 !important;
}
.icon-button:hover {
    background-color: rgb(0, 48, 0) !important;
}
.front {
    background-color: rgba(0, 0, 0, .5) !important;
}
.tag-preview-box {
    border-color: #00bb00 !important;
    background-color: rgba(0, 35, 0, .5) !important;
}
.tag-preview-box.not-usable {
    background-color: rgb(0, 0, 0) !important;
}
.tag-preview-box h4 {
    background-color: rgb(0, 35, 0) !important;
    border-bottom: 1px solid rgb(0, 86, 0) !important;
}
.tag-preview-box p {
    margin: 0 10px 10px !important;
}
.tag-preview {
    background-color: transparent !important;
    margin-top: 15px !important;
}
.preferences h2 {
    border-bottom: 1px solid #00bb00 !important;
    background-color: #002300 !important;
    color: #00bb00 !important;
}
.wrapper {
    background: rgba(0, 0, 0, .3);
}
.pages {
    line-height: 1em !important;
    vertical-align:text-top;
}
.pages a {
    padding: 1px 2px 2px 2px !important;
    text-align: center !important;
    border-radius: 20px !important;
    width: 1em !important;
    height: 1em !important;
}
.pages a:hover {
    background-color: rgba(0, 8, 0, .3) !important;
}
.pages a.next, .pages a.previous {
    margin: 0 !important;
    text-align: left !important;
    border-radius: 0 !important;
    font-weight: bold !important;
    width: auto !important;
    height: auto !important;
}
.pages a.next:hover, .pages a.previous:hover {
    background-color: transparent !important;
}
.pages span.cur {
    margin: 0 3px !important;
    padding: 1px 2px 3px 2px !important;
    width: 1em !important;
    height: 1em !important;
}
.pages span.dots {
    margin-right: 5px !important;
}
.boardheader h1 {
    font-weight: 400 !important;
}
.modal {
    background-color: rgba(0, 0, 0, .9) !important;
    border: 2px solid #005600 !important;
}
.modal .title {
    background-color: rgb(0, 35, 0) !important;
    color: #00bb00 !important;
}
.modal .content .notification {
    background-color: rgba(0, 38, 0, .7) !important;
}
.modal .content .notification .datetime {
    color: #00bb00 !important;
}
.modal .content .notification a {
    background-color: transparent !important;
}
.modal .content .buttons {
    background-color: transparent !important;
    box-shadow: none !important;
}
.loading img {
	color: red !important;
    filter: hue-rotate(100deg) !important;
}
.toast {
    background-color: rgba(0, 56, 0, .9) !important;
    border: 1px solid #008800;
    color: #00bb00 !important;
}
.unread-notifications {
    color: rgb(0,35,0) !important;
    background-color: #008800 !important;
    width: 14px !important;
    height: 14px !important;
    line-height: 14px !important;
    border: 1px solid black  !important;
}
#topbar {
    background-color: #002300 !important;
    border-bottom: 1px solid #005600 !important;
    border-top: none !important;
    max-width: calc(100% - 3px) !important;
    box-shadow: none !important;
}
#topbar .right a, #topbar .right button {
    border-left: 1px solid rgba(0, 86, 0, .7) !important;
}
#topbar .right a {
    color: #008800 !important;
    text-decoration: none !important;
}
#topbar .right a:hover {
    color: #00bb00 !important;
}
#e-sidebar-hide, #topbar .right a, #topbar .right button {
    background-color: transparent !important;
}
#e-sidebar-hide {
    border-right: 1px solid rgba(0, 86, 0, .7) !important;
}
#postoptions {
    background-color: rgba(0, 0, 0, .7) !important;
    border-color: #00bb00 !important;
    margin-top: 10px !important;
}
#title img {
    display: none;
}
#front iframe {
    display: none;
}
#front #logo {
    display: none;
}
#tabs {
    border-color: #005600 !important;
}
#tabs li {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border-color: #005600 !important;
}
#tabs li a:hover {
    background: none !important;
}
#sidebar {
    background-image: none !important;
    background-color: rgba(0, 0, 0, .8) !important;
    border-right: 1px solid #008800 !important;
}
#sidebar a.logo {
    font-weight: bold;
    margin: 10px auto 10px auto;
}
#sidebar a.logo:hover {
    text-decoration: none !important;
    text-shadow: -1px -1px 0 rgb(0, 80, 0), 1px -1px 0 rgb(0, 80, 0), -1px 1px 0 rgb(0, 80, 0), 1px 1px 0 rgb(0, 80, 0);
}
#sidebar a.logo img {
    display: none;
}
#sidebar .sq-buttons a, #sidebar .sq-buttons button {
    color: #008800 !important;
    border: none !important;
    border-radius: 50% !important;
    background-color: rgb(0, 35, 0) !important;
}
#sidebar .sq-buttons a:hover, #sidebar .sq-buttons button:hover {
    color: #00bb00 !important;
    background-color: rgb(0, 28, 0) !important;
}
#right {
    background-image: none !important;
    background-color: rgba(0, 0, 0, .5) !important;
    margin: auto 10px !important;
    min-width: calc(100% - 220px);
    overflow-x: hidden;
    max-width: none !important;
    border: none !important;
    padding-top: 10px !important;
}
#right.search {
    padding: 0px !important;
}
#right.search iframe {
    margin-top: 10px;
}
#tabchooser li {
    background: rgba(0,40,0, .7) !important;
    border-bottom: 1px solid #00bb00 !important;
}
#tabchooser li.tab:hover {
    background: rgba(0,68,0, .8) !important;
}
#tabchooser li.tab.cur {
    border-color: #00bb00 !important;
    border-bottom: 1px solid transparent !important;
    background: none !important;
}
#mobile_menu {
    background-color: #002300 !important;
}
#postbuttons {
    background-color: #002300 !important;
    border-color: #00bb00 !important;
}
#postbuttons button:hover {
    background-color: rgb(0,48,0) !important;
}
#color-buttons {
    border-color: #00bb00 !important;
    background-color: rgb(0, 35, 0) !important;
}
#color-buttons button.icon-palette:hover {
    background-color: rgb(0,48,0) !important;
}
#color-buttons button.icon-palette.black {
    color: rgba(0, 0, 0, .7) !important;
}
#color-buttons button.icon-palette.black:hover {
    color: black !important;
}
#color-buttons button.icon-palette.gray {
    color: rgba(128, 128, 128, .7) !important;
}
#color-buttons button.icon-palette.gray:hover {
    color: gray !important;
}
#color-buttons button.icon-palette.white {
    color: rgba(255, 255, 255, .7) !important;
}
#color-buttons button.icon-palette.white:hover {
    color: white !important;
}
#color-buttons button.icon-palette.brown {
    color: rgba(165, 42, 42, .7) !important;
}
#color-buttons button.icon-palette.brown:hover {
    color: brown !important;
}
#color-buttons button.icon-palette.red {
    color: rgba(255, 0, 0, .7) !important;
}
#color-buttons button.icon-palette.red:hover {
    color: red !important;
}
#color-buttons button.icon-palette.orange {
    color: rgba(255, 165, 0, .7) !important;
}
#color-buttons button.icon-palette.orange:hover {
    color: orange !important;
}
#color-buttons button.icon-palette.yellow {
    color: rgba(255, 255, 0, .7) !important;
}
#color-buttons button.icon-palette.yellow:hover {
    color: yellow !important;
}
#color-buttons button.icon-palette.green {
    color: rgba(0, 128, 0, .7) !important;
}
#color-buttons button.icon-palette.green:hover {
    color: green !important;
}
#color-buttons button.icon-palette.blue {
    color: rgba(0, 0, 255, .7) !important;
}
#color-buttons button.icon-palette.blue:hover {
    color: blue !important;
}
#color-buttons button.icon-palette.purple {
    color: rgba(128, 0, 128, .7) !important;
}
#color-buttons button.icon-palette.purple:hover {
    color: purple !important;
}
#color-buttons button.icon-palette.pink {
    color: rgba(255, 192, 203, .7) !important;
}
#color-buttons button.icon-palette.pink:hover {
    color: pink !important;
}
#tags {
    padding-top: 1em;
    background-color: rgba(0, 0, 0, .5) !important;
}
#tags h2 {
    margin: 2em 0 1em 0 !important;
}
#post {
    padding: 20px 0 !important;
}
#boardselector div {
    background-color: rgba(0, 12, 0, .9) !important;
    border: 2px solid #005600 !important;
}

/* ad hiding */
 #banner_ad_bottom {
    display: none;
}
#right .threads div[style="text-align:center"] {
    display: none;
}
#right div[style="text-align:center"] {
    display: none;
}
#right-edge {
    display: none;
}
#right div[class="center"] p {
    visibility: hidden;
    height: 0px !important;
}
#front div[style="text-align:center"] {
    display: none;
}

/* mobile styles */
 @media only screen and (max-width: 800px) {
    span.pages {
        line-height: inherit !important;
    }
    span.pages span.cur {
        background: transparent !important;
    }
    span.pages a:hover {
        background: transparent !important;
    }
    .thread .linkbutton {
        margin: 1px !important;
    }
    .messageoptions {
        background-color: rgba(0, 0, 0, .9) !important;
        border-radius: 2px !important;
        border: 1px solid #005600 !important;
        padding: 0 !important;
    }
    .navigationbar.top {
        border: none !important;
        padding: 0 !important;
    }
    .navigationbar.top .replybutton button {
        margin: 2px auto !important;
        width: 100% !important;
        font-weight: bold;
    }
    .threads.style-box {
        background-color: transparent !important;
    }
    #front {
        margin: 20px 5px auto auto !important;
    }
    #right {
        margin: 20px 0px auto 0px !important;
    }
    #sidebar  {
        background-color: rgba(0, 0, 0, .93) !important;
    }
    #sidebar .boardlist span.nsfwinfo {
        position: relative !important;
        color: white !important;
        background-color: red !important;
    }
    #display_postform {
        margin: 2px auto !important;
        width: 100%;
        font-weight: bold;
    }
}
}

