style.css:
@-moz-document url-prefix("http://www.quotev.com/") {
/* Written by Phoenix Enero. */
/* Enable user-select (because honestly, fuck those people who keep disabling them.) */
* {
  -webkit-user-select: text !important;
  -moz-user-select: text !important;
  -ms-user-select: text !important;
  user-select: text !important;
  cursor: auto !important;
}

/* Use less ugly fonts. */
#p_cview .story_title {
  font-family: Helvetica, sans-serif;
}

/* Display selections (fuck you Quotev.) */
::selection {
  background: #38d !important;
  color: #fff;
}

::-moz-selection {
  background: #38d !important;
  color: #fff;
}

::-webkit-selection {
  background: #38d !important;
  color: #fff;
}

/* Lower contrast for much easier readability.
 * Colors from Solarized theme.
 */
body {
  background-color: #EEE8D5;
  color: #174E5A;
}

/* Remove ridiculous web 2.0 styles */
a {
  font-weight: normal;
}

div.story_text {
  font-size: 18px;
  text-align: justify;
  padding: 1em 7em 1em;
  text-rendering: optimizeLegibility;
}
    
.story_text * {
  line-height: 1.75
}

#mainbar {
  color: #eee;
  background-color: #222 !important;
  box-shadow: none !important;
  border-radius: 0 !important;
  border: 0 !important;
}

#descr {
  background-color: #FDF6E3 !important;
}

div.mainbar a, 
div.mainbar a:visited, 
div.mainbar a:link {
  color: #eee;
  font-weight: normal;
}

#footer_pages {
  font-weight: normal;
  font-variant: normal;
  letter-spacing: 0;
}

}
