style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.thegamegenie.com") {

body, span, #logo, .headerbar, .row, .post,
.topiclist, .forums, .forabg, .navbar, .forumbg,
.panel, form, input, label, blockquote, span,
.button1, .button2, .button3, 
.pagination a, li a:hover
{
background: #000 !important;
}
.postlink
{
border-bottom-color: #000 !important;
}
.postlink:hover
{
text-decoration: underline !important;
}
a, dd, dt, strong, p, input, label, span,
h1, h2, h3, h4, h5, .content, .post,
.author, .signature, a:link
{
color: #FFF !important;
}
}
