style.css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
    #discover_button, .compose-button, .pinned_sidebar_footer, .sidebar_link[href="/explore"], #search_field, .save-the-internet-button, .post-it-forward-button, .recommended-unit-container.blog-card-compact, .post.is_recommended, .sponsored_post, .explore-trending-badge-footer, .takeover-container, .yamplus-unit-container, .rapid-recs-container, .logo-anchor .png-logo, .logo-anchor .preload-container {
        display: none;
    }

    #tabs_outer_container .tab_notice, #tabs_outer_container .tab_notice_value {
        background-color: #bc5555!important;
        color: #fff!important;
    }

    .l-header-container--refresh {
        box-shadow: none;
        padding-top: 13px;
        padding-bottom: 13px;
    }
    
    #account_button {
        padding-right: 0;
    }
    
    li.remnant-unit-container {
        display: none;
    }
    
    .post_full:hover .post_body {
        overflow: hidden;
    }
    
    #dashboard_controls_open_blog {
        margin-top: 0;
    }
    
    .save-the-internet-button+.blog_menu .popover_blogs {
        top: 0!important;
    }
    
    .post-typography-update .post .post_content h1, .post-typography-update .post .post_content h2, .post-typography-update .post .post-content h1, .post-typography-update .post .post-content h2, .post_full .post_title, .post-typography-update .post.is_regular .post_content .post_title {
        font-family: "Helvetica Neue", "HelveticaNeue", Helvetica, Arial, sans-serif;
    }
    
    .post_full .post_title, .post-typography-update .post.is_conversation .post_content .post_title, .post-typography-update .post.is_regular .post_content .post_title, .post-typography-update .post .post-content .title-field * {
        font-size: 22px;
        font-weight: bold;
        line-height: 1.3;
        margin-bottom: 10px;
        font-family: "Helvetica Neue", "HelveticaNeue", Helvetica, Arial, sans-serif;
    }
    
    .post_full .post_info_fence, .post_full.is_reblog .post_info_fence>a, .post .post-source-footer a.post-source-link, .post_full .post_info {
        max-width: 100%!important;
    }
    
    .svg-logo {
        display: none;
    }
    
    .logo-anchor {
        background: url('https://i.imgur.com/pPayJbc.png') no-repeat;
        display: block;
        height: 70px;
        width: 200px;
        margin-left: 5px;
        margin-top: -3px;
    }
    
    .logo-anchor:active {
        opacity: .8;
    }
    
    .l-header-container {
        box-shadow: none!important;
    }
    
    .logged_in .l-header {
        max-width: 925px!important;
        padding: 0 20px 0 0!important;
    }
    
    .l-header-container--refresh .l-header .logo.logo-redesign {
        margin-left: 0!important;
    }
    
    .post_full .post_header .post_info .post_info_link:first-child, .post-form--header .tumblelog-select .caption {
        color: #A1A1A1;
    }
    
    .post-composer_note-post .note_item, .post_full.is_note .post-body .note_item, .post_full.is_note .post_body .note_item, .post_brick.is_note .note_item {
        border-color: #ccc;
    }
    
    .post_full.is_note .nipple, .post-composer_note-post .nipple, .post_brick.is_note .nipple {
        border-left: 8px solid #ccc;
    }
    
    .post_full.is_note .nipple::after, .post-composer_note-post .nipple::after, .post_brick.is_note .nipple::after {
        display: inline;
        position: absolute;
        content: "";
        width: 0;
        height: 0;
        border-top: 8px solid transparent;
        border-bottom: 8px solid transparent;
        border-left: 8px solid #f2f2f2;
        right: 1px;
        top: -8px;
    }
}
