style.css:
@-moz-document domain("youtube.com") {
body {
    text-transform: none !important;
    background: url(http://colourlovers.com.s3.amazonaws.com/images/patterns/1723/1723551.png) repeat #eaeaea !important;
}
.html5-play-progress, .ytp-play-progress {
   background: #FF80E5 !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, #FF80E5 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,#FF80E5)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,#FF80E5 100%) !important;
}
/*.html5-load-progress, .ytp-load-progress {
   background: #404040 !important;
}*/
/* actual scrubber button */
.html5-scrubber-button, .ytp-scrubber-button {
   background: url("http://i1378.photobucket.com/albums/ah92/lilypippili/TinyCreepyFairy_zpsd58b1653.png") ! important;
   background-repeat: no-repeat ! important;
   width: 32px ! important;
   height: 46px ! important;
   border: none ! important;
   margin-left: -15px ! important;
   margin-top: -32px ! important;
}
/* scrubber button on hover */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover {
  background: url("http://i1378.photobucket.com/albums/ah92/lilypippili/TinyCreepyFairy_zpsd58b1653.png") ! important;
   background-repeat: no-repeat ! important;
   width: 32px ! important;
   height: 46px ! important;
   border: none ! important;
   margin-left: -15px ! important;
   margin-top: -32px ! important;
}
/* volume control */
.ytp-volume-slider-foreground:before {
   background: #FF80E5 !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, #FF80E5 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,#FF80E5)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,#FF80E5 100%) !important;
}

#yt-masthead-container {
    background: rgba(255,225,244,1) !important;  
    border-bottom-color: rgba(240, 91, 148, 0.35) !important;
    -webkit-box-shadow: inset 5px -5px 19px -2px 
    rgba(255,175,250,1);
    -moz-box-shadow: inset 5px -5px 19px -2px 
    rgba(255,175,250,1);
    box-shadow: inset 5px -5px 19px -2px 
    rgba(255,175,250,1);
    padding-top: 2px;
    padding-bottom: 3px;
}

#appbar-nav {
min-width: 500px;
text-align: center;
-moz-transition: margin-top .1s;
-webkit-transition: margin-top .1s;
transition: margin-top .1s;
background: rgba(255,225,244,1) !important;
-webkit-box-shadow: inset 5px 5px 19px -2px 
rgba(255,175,250,1);
-moz-box-shadow: inset 5px 5px 19px -2px 
rgba(255,175,250,1);
box-shadow: inset 5px 5px 19px -2px 
rgba(255,175,250,1);
}

.yt-card {
margin: 10px 0;
border: 0px;
border-radius: 17.5px;
background: rgba(260,260,260,0.9);
box-shadow: -5px -2.5px 10px rgba(0, 0, 0, 0.25);
-moz-box-sizing: border-box;
box-sizing: border-box;
}

.webkit .yt-ui-ellipsis-2 {
-webkit-line-clamp: 2;
background: rgba(250,250,250,0)
}
  
.webkit .yt-ui-ellipsis {
display: -webkit-box;
-webkit-box-orient: vertical;
}

#yt-masthead #logo {
background: no-repeat url(http://i1378.photobucket.com/albums/ah92/lilypippili/rsz_youtube_logo_by_chocoholic01-d40n161_zpsf6031c31.png);
background-size: auto;
width: 112px;
height: 40px;
}

#yt-masthead-user .sb-notif-on .yt-uix-button-content {
position: absolute;
top: -2px;
left: 14px;
z-index: 100;
min-width: 15px;
border-bottom: 1px solid #fff;
border-left: 1px solid #fff;
background: #5BCDC6;
color: #fff;
line-height: 15px;
text-align: center;
opacity: 1;
border-radius: 2px;
}

#appbar-nav .epic-nav-item-heading {
height: 40px;
padding: 0;
line-height: 40px;
-moz-box-sizing: border-box;
box-sizing: border-box;
border-color: rgba(240, 91, 148, 1) !important;
}

#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
height: 40px;
padding: 0;
line-height: 40px;
-moz-box-sizing: border-box;
box-sizing: border-box;
font-weight: normal;
color: #47A59F;
border-color: rgba(240, 91, 148, 1) !important;
}


.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text, .guide-collection-item .guide-item.guide-item-selected {
background: rgba(240, 91, 148, 1);
color: #fff;
}

.guide-item {
color: #47A59F;
height: 28px;
font-size: 12px;
}

.yt-scrollbar ::-webkit-scrollbar-thumb {
background: rgba(240, 91, 148, 1);
}
.yt-scrollbar ::-webkit-scrollbar-thumb {
background: rgba(240, 91, 148, 1);
}
.yt-scrollbar ::-webkit-scrollbar-thumb {
background: rgba(240, 91, 148, 1);
}

a.yt-uix-button-epic-nav-item.partially-selected:hover, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
height: 29px;
line-height: 29px;
border-bottom: 3px solid;
border-color: rgba(240, 91, 148, 1) !important;
padding-bottom: 0;
display: inline-block;
}

a.yt-uix-button.yt-uix-button-epic-nav-item, button.yt-uix-button-epic-nav-item, .epic-nav-item {
border: none;
padding: 0 3px 3px;
cursor: pointer;
background: none;
color: #47A59F;
font-size: 12px;
font-weight: normal;
height: 29px;
line-height: 29px;
vertical-align: bottom;
-moz-box-sizing: content-box;
box-sizing: content-box;
border-radius: 0;
display: inline-block;
}

.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
color: #5BCDC6;
}
.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
color: #5BCDC6;
}
a {
cursor: pointer;
color: #5BCDC6;
text-decoration: none;
}
.feed-item-container:hover .feed-item-header a, .feed-item-container:hover a.yt-user-name {
color: #5BCDC6;
}

.branded-page-module-title a:visited, .branded-page-module-title a {
color: #47A59F;
}

.branded-page-related-channels h3 a, .branded-page-related-channels h3 {
width: 100%;
color: #5BCDC6;
text-decoration: none;
}

.yt-thumb img {
font-size: 13px;
outline: none;
-ms-interpolation-mode: bicubic;
border: 0;
border-radius: 15px;
}

#watch7-sidebar .video-list-item a:visited .title {
color: #5BCDC6;
}
.video-list-item a:visited .title {
color: #5BCDC6;
}
.video-list .video-list-item .title {
color: #5BCDC6;
font-size: 13px;
font-weight: bold;
}

.video-list .video-list-item .description, .video-list .video-list-item .stat {
color: rgba(200,0,200,1);
font-size: 11px;
}

body #footer-container {
margin-top: -141px;
background-color: rgba(260,260,260,0.85);
border-top: 1px solid #e2e2e2;
}

#footer-logo .footer-logo-icon {
vertical-align: middle;
background: no-repeat url(http://i1378.photobucket.com/albums/ah92/lilypippili/rsz_youtube_logo_by_chocoholic01-d40n161_zpsf6031c31.png);
background-size: auto;
width: 112px;
height: 40px;
}
}
