style.css:
@-moz-document url-prefix(http://instasync.com/) {
a.navbar-brand {
color: #000;
visibility:hidden;
}
a.navbar-brand:before {
content: "AudSync";
visibility:visible;
color: #fff;
}
body.dim {
  background-color: white;
  background-image: url("http://i.imgur.com/5rzxBH0.jpg");
}
body.dim .navbar-default {
  background-image: linear-gradient(to right, #326432 0, #000000 100%);
  border-bottom: #ff96c8
}
.navbar>.container .navbar-brand, .navbar>.container-fluid .navbar-brand {
  color: #fff
}
.navbar-default .navbar-nav>li>a {
  color: #fff
}
.room-container .room-component-row .video-controls .nav-tabs {
  background-color: #50c875;
}
body.dim .room-container .room-component-row .video-controls .tab-content {
  background-color: #50c875;
}
body.dim .room-container .room-component-row .col-sm-4 {
  background-color: #50c875;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {
  background-color: #96ffc8;
  color: #000000;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active {
  background-color: #326432
}
#poll_tab .poll.active, #poll_column .poll.active {
  background-color: #96ffc8
}
.btn-primary {
background-image: linear-gradient(to bottom,#326432 0,#000000 100%);
border-color: #505050;
}
.btn-primary:hover, .btn-primary:focus {
background-color: #000000;
background-image: linear-gradient(to bottom,#326432 0, #000000 100%);
color: #ffffff

}}
