style.css:
@-moz-document domain("trello.com") {
.week-horizontal-canvas .day-column .calendar-day {
  overflow-y: scroll;
  overflow-x: hidden;
}

/* Collapse Done [4th Card] */
.page-index .list:nth-child(4) {  
  height: 200px;
}
.page-index .list:nth-child(4) .list-card {
  background: #ccc;
}
}
