@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("msn.com") {
html, body {background: url(http://ya-vidoc3000.narod.ru/images/7.jpg) no-repeat center center fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  /*Заголовок*/
  .head {background: none !important; border: none !important;}
  #header-search form, #header-search .qc, #header-search #q {background: none !important;}
  /*Контент*/
  #maincontent {background: none !important;}
  /*Бывшая красная полоска для быков*/
  #breakingnewsinner {background: none !important; background-color: white !important; opacity: 0.7 !important; color: black !important;}
  #breakingnewsinner, #breakingnewsinner a {color: black !important;}
  /*Реклама*/
  .widead.non-empty-ad, .todaymodule.layout-medium .todayshowcasead, .yap-yandex_ad_R-116830-5-msn300x250, .yap-yandex_ad_R-116830-5-msn300x250 .yap-layout {display: none !important;}
  /**/
  #maincontent, .stripeouter, .todaymodule, .todaystripe {background: none !important;}
  /**/
  .sectioncontent {opacity: 0.7 !important;}
  .sectioncontent:hover {opacity: 1 !important;}
  div.normalsection.loaded {display: none !important;}
  .section {background-color: white !important; opacity: 0.7 !important;}
  .section:hover {opacity: 1 !important;}
  div.stripe.full-width {opacity: 0.7 !important;}
  div.stripe.full-width:hover {opacity: 1 !important;}
}
