@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
  body {background: url(http://ya-vidoc3000.narod.ru/fon/reWalls.com-29567.jpg) fixed; -webkit-background-size: cover; background-size: cover; font-family: Time newroman; font-size: 13px !important;}
  a {color: white !important; text-decoration: none;}
  a:hover {color: white !important; text-decoration: none;}
/*Заголовок*/
  #main-header {background: none; padding-top: 20px; color: red !important;}
  #Head {background: none !important; color: white !important; opacity: 0.7 !important;}
  #Head a {color: white !important; font-size: 15px !important;}
  #Head a:hover {color: white !important; background-color: black !important; text-decoration: none !important;}
  .SiteTitle {background-color: black !important;}
  /*Контент сайта*/
  #main-article {background: none !important; background-color: black !important; opacity: 0.7 !important; color: white !important;}
  /*Убираем лишнее*/
  .ad, #social {display: none !important;}
  /*Меню сайта*/
  #left-sidebar > * {background-color: black !important; opacity: 0.7; position: fixed !important; color: white !important;}
  #left-sidebar > *:hover {opacity: 1;}
  /*Таблица со стилями пользователя*/
  ul, dl, h2, p, h3 {background-color: black !important; opacity: 0.7; text-shadow: 1px 1px 2px black, 0 0 1em red; font-size:15px; color: white !important;}
  #Panel {padding-top: 20px;}
  .style-brief-text {background-color: black !important; opacity: 0.7;}
  #additional-info-text, #info-tables {background-color: black !important; opacity: 0.7; max-height: 50em;}
  #Content {opacity: 0.7 !important; color: white !important; background-color: black !important;}
  footer {float: right !important;}
  footer a:hover {background-color: red;}
  .style-brief-text a {color: red;}
  .Discussion:hover, .ItemContent:hover, .DataList:hover {opacity: 1;}
  #Content {font-size: 15px;}
  #Content:hover {opacity: 1;}
  .Item.Read {background-color: black !important;}
  /*какой-то мусор*/
  #related {display: none !important;}
  /**/
  .PageControls {background-color: black !important; opacity: 0.7 !important; text-align: center !important;}
  .HomepageTitle {color: white !important; background-color: black !important;}
  #long-description, #control-panel {background-color: black !important; opacity: 0.7 !important; color: white !important;}
  .author-styles {border: none !important; color: white !important;}
  .author-styles table {border: none !important; width: 100% !important;}
  .author-styles thead {background-color: black !important;}
  .author-styles tr {background-color: black !important;}
  .author-styles tr:hover {background-color: red !important;}
  /*Кноака на форуме*/
  .Button {background: none !important; border: none !important; text-shadow: none !important; box-shadow: none !important; background-color: black !important;}
  /*Меню на форуме*/
  .MenuItems {background-color: black !important;}
  .MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {color: white !important;}
  .MenuItems a:hover {background-color: white !important; color: black !important;}
  .FlyoutMenu {background: none !important; background-color: black !important;}
  .HasNew {background-color: red !important;}
  .Discussion:hover, .MessageList:hover, .Message:hover {opacity: 1 !important;}
  code, pre {background-color: black !important; opacity: 1 !important;}
  /**/
  li:first-letter {color: red !important;}
  /**/
  table.author-styles th:nth-child(2), table.author-styles td:nth-child(2), table.author-styles th:nth-child(4), table.author-styles td:nth-child(4) {background: #666;}
  table.author-styles td:nth-child(7) {height: 30px; width: 160px !important; text-align: right !important;}
  table.author-styles td:nth-child(7) a[href*="edit"], #control-panel a[href*="edit"] {background: lightgreen; border: 2px solid black; font-weight: bold; padding: 5px;}
  table.author-styles td:nth-child(7) a[href*="delete"], #control-panel a[href*="delete"] {background: red; border: 2px solid black; font-weight: bold; padding: 5px;}
  table.author-styles td:nth-child(7) a[href*="stats"], #control-panel a[href*="stats"] {background: blue; border: 2px solid black; font-weight: bold; padding: 5px;}
}
