style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("share.dmhy.org") {
body{
  background-color:#F4F4F4!important;
  color:#333!important;
  font:14px/22px "Microsoft Yahei",arial,sans-serif;
}
.container,.main,.headerright{
  background:url("data:image/png;base64,") 0 0 repeat !important;
} 
#mini_jmd,.table,#team-dialog,.topics_bk,#cms-13,#cms-65{
    margin-bottom: 10px;
    margin-top: 0px;
    margin-left:-5px;
    border: 1px solid #BBB;
    border-radius: 3px;
    box-shadow: 0px 0px 3px #CECECE;
    background-color: #FFF;
    padding: 10px;
    width:960px;
}
/******************下载页面******************/

#tabs-1 a{
  color:#247;
  text-decoration: underline;
}
#tabs-1 a:hover{
  color:red;
}
.topic-title .right{
  float:none!important;
}
.file_list{
  max-height: none!important;
  height:auto!important;
  overflow:auto!important;
}

/*隐藏蛋蛋播放器链接(试过，并不好用)*/
#tabs-1 p:nth-of-type(4),.resource-info li:nth-of-type(4){
  display:none!important;
}
/******************宽度**********************/
.main{
  margin: 0 auto !important;
  width: 980px !important;
  min-width: 980px !important;
  position: relative!important;
}
.bg{
  margin:0;
  min-width: 0!important;
}
/******************链接******************/
a{
  color:#2874A6!important;
}
a:hover,a:active,a:focus{
  color:#F00!important;
  text-decoration:underline!important;;
  border:none!important;
}

/******************页头******************/
.bg{
  position:relative!important;
}
.header{
  background:linear-gradient( #105D7A, #023243) repeat scroll 0% 0% transparent!important;
  height:215px!important;
}
.headerleft img{
  display: none!important;
}
.headerleft a{
  display:block!important;
  margin:0;
  width:200px;
  height:80px;
  position: absolute!important;
  top:60px;
  background: url("data:image/png;base64,")no-repeat 0 0 !important;
}
.headerleft{
  width:980px!important;
  margin:0px auto!important;
  float:none!important;
}
.headerright{
  position:absolute!important;
  top:0;
  width:100%!important;
  max-width: none!important;
  height:34px!important;
  padding:0!important;
  border-bottom:1px solid #E6E6E6!important;
  background:url("data:image/png;base64,") repeat-x 0 0!important;;
}
.headerright .links{
  background:none!important;
  height:24px!important;
  width:980px;
  margin:10px auto 0 auto!important;
  padding-right:5px;
  float:none!important;
  text-align: right!important;
}  
.headerright br{
  display: none!important;
}
/******************搜索框******************/
.quick_search {
  width: 460px !important;
  margin: 0 0 5px !important;
  position: absolute!important;
  top:-170px;
  right:10px;
  padding:0px;
  text-align:left!important;
  border:none!important;
  background:url("data:image/png;base64,")no-repeat 0 0 ;
}
.quick_search .quick_input {
float: left;
margin: 6px 0px 0px 6px;
border: 0px none;
font-size: 14px;
width: 338px;
padding: 0px 0px 0px 27px;
height: 28px;
line-height: 28px;
background-position: 5px -292px;
}
.quick_search .quick_input:focus{
  background-color:#fff;
}
.quick_search .formButton{
float: left;
margin-top: 6px;
margin-left: 4px;
display: block;
width: 80px;
height: 28px;
cursor: pointer;
background:transparent;
color:rgba(0,0,0,0);
border:none;
}
.quick_search .formButton+a{
  color:#CEDCE1!important;
  position:relative;
  top:5px;
}
#AdvSearch{
  background:none;
  width:320px;
  position: relative;
  top:-18px;
  left:60px;
}
#AdvSearch p{
  display: block;
  margin:0!important;
  color:#CEDCE1;
}
  #AdvSearch p:last-child{
  float:right!important;
  margin-top:-20px!important;
}
/***********节目单***********/
.jmd{display:none!important;}
.jmd_base{display: block!important;}
/***********七日节目单*******
.jmd{display:none!important;}
.jmd_base{display: block!important;}
*********四日节目单***********
.jmd_base{display:none!important;}
.jmd{display: block!important;}
************************/

.jmd .today th,.jmd_base .today th{
  color:red!important;
}
.jmd a,.jmd_base a{
  border:none!important;
  display: inline-block!important;
  margin:2px 8px!important;
}
#mini_jmd{
  position: relative!important;
  left:0px;
  top:0px;
  width:960px!important;
  padding-top:30px;
}
#mini_jmd .fr a{
  background:url("data:image/png;base64,")no-repeat right top;
  display: block!important;
  height:70px;
  width:90px;
  position: absolute;
  top:-3px;
  right:-3px;
  text-indent:-999em!important;
  visibility: visible!important;
}
#mini_jmd .nav_title{
  visibility: hidden!important;
  height:0px!important;
}
.jmd th,.jmd_base th{
  background: none!important;
  color:#000!important;
  white-space:nowrap;
  padding-right:20px!important;
}
.jmd tr:hover td,
.jmd tr:hover th,
.jmd_base tr:hover td ,
.jmd_base tr:hover th{
    background:#E4EFFC!important;
  }

/***********每周番组,完结番组***********/
#cms-13,#cms-65{
  margin:10px auto!important;
}
.weekly_list_title a,.bgm-anime-title a{
  color:#fff!important;
}
.weekly_list td+td{
  width:885px;
}
#tue+td,.bgm-rowhead-tue+td{background:#66F;}
#wed+td,.bgm-rowhead-wed+td{background:#800;}
#thu+td,.bgm-rowhead-thu+td{background:#c60;}
#fri+td,.bgm-rowhead-fri+td{background:#990;}
#sat+td,.bgm-rowhead-sat+td{background:#006;}
#sun+td,.bgm-rowhead-sun+td{background:#360;}
#mon+td,.bgm-rowhead-mon+td{background:#906;}
#long+td{background:#333;}
/***********主菜单***********/
.top_sort{
  position: relative!important;
  top:40px;
  left:-4px;
  width:980px;
  margin:0 auto;
  border-radius: 2px;
}
.top_sort .menu{
  border-radius: 2px;
  height: 40px;
  width:730px;
  background: linear-gradient(#FFF, #E1E1E1) repeat scroll 0% 0% transparent;
  padding-right:250px;
}
.top_sort .menu ul{
  line-height: 40px!important;
}
.top_sort .nav li a:hover,.top_sort .nav ul a{
  background:#F4F4F4!important;
  text-decoration: none!important;
}
.top_sort .nav ul li:hover a,.top_sort .nav ul li:hover font{
  display: block;
  background-color: #333537!important;;
  text-shadow: 0px -1px 1px rgba(0, 0, 0, 0.5);
  box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
  border-radius: 3px;
  color: #FFF !important;
  text-decoration: none!important;
 }
.menu li{
  border-right: 1px solid #CCC;
  height: 40px;
}
.menu li font{
  color:#000!important;
}
.menu .sort-2:before,.menu .sort-3:before,.menu .sort-4:before,.menu .sort-9:before{
  content:"▼";
  font-size:10px;
  color:#000;
  padding-right:10px;
}
/********联盟********/
#expand-button{
  position: absolute;
  display: block;
  top: 7px;
  right: 10px;
  width:50px!important;
  background:linear-gradient(#FFF, #E1E1E1) repeat scroll 0% 0% transparent!important;
  padding:0px!important;
  margin:0!important;
  border:1px solid #bbb;
}
#expand-button div{
  margin:0!important;
}
#team-dialog{
  margin-top:40px;
  margin-left:0px;
}
#team-inner {
  width:960px!important;
}
/******************表格******************/
.odd,.even,.odd td,.even td{
  background-color:#fafafa!important;
}  
.odd:hover td,.even:hover td{
  background-color:#f2f2f2!important;
}
#topic_list{
  border-collapse: collapse;
  border-spacing: 0px; 
  width:980px!important;
  margin-left:-10px!important;
}
.odd,.even{
  border-bottom: 1px solid #ddd!important;
}

#topic_list th{
  height:24px!important;
  padding: 0!important;
  color:#000;
  font-weight: normal;
  border-bottom: 1px solid #DDD;
  background: none repeat scroll 0% 0% #EEE!important;
}
#topic_list~.nav_title{
  float:none!important;
  text-align: center!important;
}
/************小标题************/
img[alt="rss"]{
  vertical-align: middle!important;
}
.table .nav_title,#cms-13 .nav_title,#cms-65 .nav_title{
  border-bottom: 1px solid #AAA;
  font-weight: bold;
  color: #1A7BB9;
  height: 32px;
  line-height: 32px;
  margin: -10px -10px 0 -10px;
  padding: 0px 10px;
  font-size: 14px;
  background: linear-gradient(#FFF, #E1E1E1) repeat scroll 0% 0% transparent; 
} 
#topic_list~.nav_title{
  margin-bottom: -10px!important;
  border-top:1px solid #eee
}
/**********详情页***********/
.topics_bk{
  width:958px;
}
.topic-nfo img{
  max-width:760px!important;
}
/********页脚********/
.footer{
  background:linear-gradient(#105D7A, #023243) repeat scroll 0% 0% transparent;
  padding-bottom: 60px;
  margin: 0px;
} 
.footer+div,.footer+div+div{
  position:absolute;
  bottom:20px;
  width: 100%;
}
.top a{
  color:#fff!important;
}
.footer:after{
  content:"仿极影 by 天涯倦客";
  font-size: 12px;
  color:#4C9ED9;
}
/*******广告*******/
.ad,[id$="_ad"],[id^="id01_"],#before-comment,#DM_ad+br,a[name="description-end"]+br{
    display:none!important;
  }
}
