@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ehtio.es") {
 
body{background-color: #369; }
#series #desplegado {border-bottom:0px solid #AAA;}

header #cabecera .divcabecera#iconos .wrapper {margin-right: 9px; }
    
  
header #cabecera {height: 60px; background-color: #AAA; width: 800PX; margin: auto; text-align: center;}
