style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.cnbeta.com"),domain("cnbeta.com") {
  
/* 需要 和 
** https://userstyles.org/styles/107611/2017
** 配合使用！
***/  

/*[[scroll]]*/
html {
    background: url("data:image/jpeg;base64,");
    background-repeat: repeat;
}
.cnbeta-article-wrapper {
    background: none;
}
.cnbeta-swiper-headlines .swiper-slide:hover h2 a, .cnbeta-swiper-headlines .swiper-slide .highlight, .cnbeta-swiper-article-related .swiper-slide:hover h2 a, .cnbeta-swiper-article-related .swiper-slide .highlight, .cnbeta-update-list .item dt a:hover ,a:hover {
    color: #999 !important;
}
blockquote{
    background: #333 !important;
    color: #999 !important;
    border-left: 4px solid #777;
}
::selection{background:#aaa;color:#222;text-shadow:none;}  
::-moz-selection{background:#aaa;color:#222;text-shadow:none;}
::-webkit-selection{background:#aaa;color: #222;text-shadow:none;} 
	
.cnbeta-update-list .item {
    background: transparent;
    border-bottom-width: 0;
}
.cnbeta-update-list .item:hover,.cnbeta-article,.swiper-slide:hover .cb-group-carousel-inner {
    border-radius: 5px;
    background: #222;
    box-shadow: -1px -1px 2px #333 inset, 1px 1px 2px #111 inset;
}	
.swiper-slide:hover .cb-group-carousel-inner .headline-thumb {
    border-radius: 5px 5px 0 0 ;
    box-shadow: -1px -1px 2px #333 inset, 1px 1px 2px #111 inset;
}	
.cnbeta-swiper-headlines .swiper-slide.current .mask ,.cnbeta-swiper-headlines .mask ,.mask {
    background: transparent !important;
}
	
.cnbeta-update-list .item:hover {
    background: #222 !important;
    color:#777 !important;
}	
.cnbeta-article-body {
    color:#777;
}	
.cnbeta-article-comments-list,.cnbeta-article-body ,.cnbeta-article-comments-list-wrapper,.cnbeta-article-comments-wrapper,
.quote-comments 
	{
    background: transparent;
}	
.cnbeta-update-list .item dl img , .cnbeta-update-list .item dl img:hover {
    border: 5px solid #222;
}
img,.cb-user-info .cb-service-link,.comments-btns i ,.cb-comment-emoji-bar button{ opacity: .5;}
/* header 
{
    background: #222;
    border-bottom: 1px solid #ccc;
}*/

.pageHeader,.global-navi .nav-item a,.global-navi ,.article-summary,.quote-comments,
.cnbeta-article-comments-list-wrapper li .msg-box .inner,.topSearchBox,.cb-comment-emoji-bar{
    border-color: #333;
}
#main-commentbox,.cnbeta-article-comments-tabs{
    border-color: transparent;
}
.cnbeta-article-comments-list-wrapper li .msg-box .inner .arrow ,.cb-comment-emoji-bar{
    background: none;
}
.cnbeta-article header.title,.cnbeta-article-comments-tabs a.active {
    background: rgba(255,255,255,.03);
    color:#777;
}
.cb-comment-post input.code ,.cb-textarea-wrapper textarea,.cb-comment-post .cb-post-button{
    background: #333;
    border-color: #444;
    color: #777;
    text-shadow: 0 0 0 rgba(0,0,0,0);
}
.pageFooter,.pageHeader  {
    background: #333;
    opacity: .58;
}	
.topSearchBox input:not([type="button"]) {
    background: #666;
    border-color: #777;
}
.cnbeta-headlines {
    background: transparent !important;
}
.cnbeta-swiper-headlines .swiper-slide.current .mask, .cnbeta-swiper-headlines .mask {
    background: #222;
}	
.cnbeta-update-list .loading-bar {
    background: #333;
}
.pagination > li > a, .pagination > li > span {
    color: #888;    
    background: #333;
    border: 1px solid #333;
}
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus ,
.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
	color: #ccc;
	background-color: #555;
	border-color: #555;
}
#goTop,li[id*="Comment_Item"] > .c-avatar::after  {
    opacity: .15 !important;
}
.cnbeta-update-list .item .meta-data .status {
    color: #777;
}
img, .cb-user-info .cb-service-link, .comments-btns i, .cb-comment-emoji-bar button {
    opacity: .5;
}
.cb-comment-post .seccode {
    border: 1px solid #333;
}
.cnbeta-article-comments-list-wrapper li .msg-box .datetime ,.tip-wrap , .quote-comments-con,.comment-html,
.hide-tips ,.quote-comments .hide-tips span, .quote-comments .hide-tips:hover,.J_commt_wait 	{
    color: #666;
}
.cnbeta-article-hot-comments-wrapper {
    background: #444;
}
.cnbeta-article-hot-comments-wrapper li .msg-box .inner {
    background: #333;
	border: 0px solid ;
}
.cnbeta-article-hot-comments-wrapper li .msg-box .arrow {
    background: none;
}
.cnbeta-side-yellow-title {
    background: #555;
}
.cnbeta-article + .cnbeta-home-side .cnbeta-home-hot-comments > h3::before {
    border-right-color: #555 !important;
}
.cnbeta-article + .cnbeta-home-side {
    opacity: 1 !important;
}
.topSearchBox input:not([type="button"]) {
   color: #eee !important;
}
.cnbeta-article-body a, .article-summary a {
    color: #777;
    border-bottom: 1px solid #777;
}
	
}

options.value:
/* 评论的滚动条 */	
[style="max-height: 1017px; overflow-y: scroll;"]#comments-box:after {
	content: "";
	width: 17.5px;
	height: calc( 100% - 305px );
	background: transparent;
	overflow: visible;
	position: absolute;
	top: 265px;
	right: 0px;
	box-shadow: -19.5px 0px 0px rgba(0,0,0,.8);
}

[style="max-height: 1017px; overflow-y: scroll;"]#comments-box:after {
    content: "";
    width: 10px;
    height: calc( 100% - 303.2px );
    background: transparent;
    overflow: visible;
    position: absolute;
    top: 263.6px;
    right: 0px;
    box-shadow: -20px 0px 0px rgba(0,0,0,.7);
}
