style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("8tracks.com") {
  
  *, .mix_card .cover{
    image-rendering:optimizequality !important;
    text-rendering: optimizelegibility !important;
  }
  
  body{
    font-family: Avant Garde,Avantgarde,Century Gothic,CenturyGothic,AppleGothic,sans-serif !important;
    background: linear-gradient(#A1CDF4 2%, #A9AFD1 95%, #A9AFD1 100%) repeat scroll 0% 0% #A9AFD1 !important;
  }
  
  
  #thin_header, #header_content, #logged_in_dropdown, .recent-mixes{
    background-color:#A9AFD1 !important;
  }
  
  #explore_button{
    background-color:#7C809B !important;
  }
  
  a.tag, .fav_track, #mix_edit_details .mixedit, #mix_edit_details .tags_editor {
    background: transparent !important;
    border: 1.5px solid #7C809B !important;
    color: #7C809B !important;
}
  
  .card{
    background: transparent !important;
    border: 2px solid #A9AFD1 !important;
    border-radius:1px !important;
  }
  
  .big_menu a.item, .big_menu .item,  .profile_menu .item.selected{
    color:#7C809B !important; 
  }
  
  .profile_menu .item{
    color:#A9AFD1 !important;
}
  
  .big_menu a.selected{
    color:#A9AFD1 !important;
    border-color:#A9AFD1 !important;
  }
  
  .big_menu a.selected::after{
   border-bottom: 7px solid #A9AFD1 !important;
  }
  
  .card .reason::after, #profile_bottom::after{
    border:none !important;
    background:transparent !important;
  }
  
  .background-blur-container{
    background:#A1CDF4 !important;
  }
  .background-blur{
    display:none !important;
  }

  .ad_card, .preroll_container, #superheader_box, #sidebar_ad, .sidebar_ad{
    display:none !important;
  }
  
  
  .big_menu::after, .card .reason, .profile_menu .item.selected::after{
   border-bottom: 3px solid #7C809B !important; 
  }
  
  #profile_top .counters a:link, #profile_bottom, a:link, .track .player, #logged_in_dropdown .menu-item, .recent-mixes .item, .end_card{
    color: #fff !important;
  }
  
  .profile_menu .item.selected, #play_area, .publish_options, #track_selection_header, #track_search_header, #selected_tracks li:nth-of-type(2n){
    background:transparent !important;
  }
  
   #logged_in_dropdown, .recent-mixes{
     border: 2px solid #A9AFD1 !important;
     border-radius: 5px !important;
     box-shadow:none !important;
   }
  
  #logged_in_dropdown .menu-item:hover{
    background:#7C809B !important;
    color: #fff !important;
  }

}
