style.css:
@-moz-document domain("gutefrage.net") {
.Survey-satisfactionModal ,
.Modal-overlay ,
.Modal-overlay:before ,
#page.is-Modal-visible:before ,
.Survey-socialBar {
	display : none !important;
}
#header {
	margin: 5px 0px;
}
.Question, .Answer, #answer-know-block {
	padding: 5px 10px;
	margin: 3px 3px;
	border-radius: 3px;
}
.Question-badge, .Answer-badge {
	width:30px;
	height:30px;
	font-size:20px;
  line-height: 30px;
	left: -30px;
	top: 0px;
	border-radius: 3px;
}
#page {
	padding: 0em 0em;
}
	
.Question-author, .Answer-author {
	font-size: 1.2em;	
}
.Question-title {
	line-height: normal;
	margin: 5px 5px;
}
.Button.Button__primary {
	height: 30px;
	line-height: 30px;
}	
.ToolBar {
	margin: 5px 0px;
}
.Question-notice{
	margin-top: 10px;
}
.Comments {
	margin: 10px 0px 0px 20px;
}
.Block-body{
	padding: 5px 20px;
	margin: 10px 10px;
}
.Answer-avatar, .Question-avatar {
	top: 5px;
	right: 10px;
	margin-top: 13px;
}
.Answer-avatar img {
	border-radius:3px;
}
.Question-avatar img {
	border-radius:3px;
}		
.Question-notice.Question-notice__noAnswers::before{
	right: 0px;
	font-size: 4em;
}
.Block{
	padding: 5px 10px;
	margin: 3px 3px;
	border-radius: 3px;
}
