options.value:
.Block-headline {
	margin-bottom: 5px;
}	
.Block-body {
	margin: 0px 0px;
	padding-left: 10px;
	padding-right: 10px;
}	
.Tabs.Tabs__simple .Tabs-navItem > a{ 
	height: 30px;
	line-height: 30px;
}
.ng-binding > strong {
	color: #0003FF;
	font-size: 1.3em;
}
.Form-error {
	margin-left: 270px;
	margin-top: -70px;
}
.Block-separator {
	margin-top: 10px;
}
