@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("shikimori.org") {
html, body {background: url(http://ya-vidoc3000.narod.ru/anime/29.jpg) no-repeat 100% 100% fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  /*Header*/
  .to-top-fix, .l-top_menu {background: none !important; border: none !important;}
  .l-top_menu .submenu {border: 1px solid pink !important;}
  .l-top_menu .menu-items, .l-top_menu .submenu > .entry {background-color: pink !important; border: 1px solid pink !important;}
  .l-top_menu .menu > .entry.selected, .l-top_menu .menu > .entry:hover {background-color: lightpink !important;}
  .main-search .input, .l-top_menu .menu > .entry:hover {background: none !important;}
  .main-search ul.popup {border: none !important;}
  .l-top_menu .menu-search, .l-top_menu .menu-userbox {border: 1px solid pink !important;}
  .main-search .input {height: 22px !important;}
  /*Content*/
  .l-page {background: none !important;}
  .b-catalog_entry .cover {background-color: white !important; border: 1px solid pink !important;}
  .inner {background-color: white !important; border: 1px solid pink !important;}
  .b-topic > .inner header, .b-topic > .inner > footer {margin: 0 !important;}
  .b-catalog_entry {max-width: 100% !important;}
  .b-topic > .b-comments, .b-comment {margin-top: 0 !important; padding-top: 0 !important;}
  .b-comment {border: none !important; margin: 0 !important;}
  /*Левая панель*/
  .headline, .midheadline, .subheadline {background: none !important; border: none !important; background-color: pink !important;}
  .block {background-color: white !important; border: 1px solid pink !important;}
  .block-list li:hover, .block-list-alt li:hover {background: none !important;}
  /**/
}
