style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org"){

    .mw-body {
    background-color: #151515 !important;
    color: #80C29C !important;
    border-color: #212223 !important;
    border-width: 1px 1px 1px 4px !important;
}  
    body {
    background-color: #151515 !important;
}

.mw-body-content {
    font-size: 1.2em;
}
    
.infobox {
    border: 5px solid #261E1E !important;
    background: none repeat scroll 0% 0% #111010 !important;
    color: #777 !important;
}    
    
.mw-body a.external {
    color: #468BFF !important;
}    
 
.catlinks {
    border: 4px solid #4B4B4B !important;
    background-color: #111010 !important;
}
     
    
    #mw-page-base {
    height: 5em !important;
    background-position: left bottom !important;
    background-repeat: repeat-x !important;
    background-image: linear-gradient(#151515 10%, #232222 100%) !important;
    
}
    
    h1, h2, h3, h4, h5, h6 {
    color: #5FBF85 !important;
}
    
a {
    text-decoration: none !important;
    color: rgb(30, 192, 129) !important;
    background: none repeat scroll 0% 0% rgba(5, 72, 39, 0) !important;
}
    
a:visited{
    color:#555 !important;
}                   
    
a:visited{
    color:#555 !important;
}
                   
    div#mw-panel div.portal div.body ul li a {
    color: #447EDD !important;
}    
  
    blockquote {
    background: none repeat scroll 0% 0% #262222 !important;
}
    
#toc, .toc, .mw-warning, .toccolours {
    background-color: #202020 !important;
    border: 2px solid #0F382F !important;
}

a {
    color: rgb(30, 192, 129) !important;
}

div.thumbinner {
    border: 1px solid #0D546C !important;
    background-color: rgba(83, 151, 195, 0.17) !important;
}    

a.new, #p-personal a.new {
    color: #E75A5A !important;
}


    
.navbox, .navbox-subgroup {
    background: none repeat scroll 0% 0% #060606 !important;
}


div#footer ul li {
    color: #050505 !important;
}

html элемент {
    border: 1px solid #CCC !important;
    background-color: #2C2C2C !important;
}    

table.ambox {
    
    border-color: #AAA #AAA #AAA #1E90FF !important;
    border-image: none !important;
    background: none repeat scroll 0% 0% #111010 !important;
    border-width: 3px 3px 1px 10px !important;
}    
    
                   
.mw-mmv-post-image {
    color: #D2D2D2 !important;
    background-color: #212121 !important;
    transition: height 1s cubic-bezier(0,0,0.67,0.51) 0s !important;
    transition-property: height !important;
transition-duration: 1s !important;
transition-timing-function: cubic-bezier(0, 0, 0.67, 0.51) !important;
transition-delay: 0s !important;
}
                   
.mw-mmv-image-metadata {
    border-top: 1px solid #545252 !important;
    background-color: #171717 !important;
}
 
.mw-mmv-image-desc {
    color: #D2D2D2 !important;
    transition: width 1s cubic-bezier(0,0,0.67,0.51) 0s !important;
}                   
                   
.mw-mmv-image-metadata-links-column {
    transition: width 1s cubic-bezier(0,0,0.67,0.51) 0s !important;
}                   
 
div#simpleSearch input {
    background-color: rgba(0, 0, 0, 0.76) !important;
    color: #FCFCFC !important;
}                   
 
.suggestions-results {
    background-color: #232020 !important;
    border: 1px solid #302F2F !important;
}
                   
.navbox th, .navbox-title {
    background: none repeat scroll 0% 0% #293D42 !important;
}
                   
div.flaggedrevs_preview {
    background-color: #232529 !important;
    color: #837D7D !important;
}                   
  
table.standard th, table.wide th, table.prettytable th {
    border: 1px solid #4B4242 !important;
    background: none repeat scroll 0% 0% #1F1F26 !important;
}                   
                   
table.standard td, table.wide td, table.prettytable td {
    border: 1px solid #473A3A !important;
}                   
                   
table.wikitable > tr > th, table.wikitable > * > tr > th {
    background-color: #2F2121 !important;
    text-align: center !important;
}
                   
table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td {
    border: 1px solid #4E4646 !important;
    padding: 0.3em 0.4em !important;
}                   
                   
table.wikitable {
    border-collapse: collapse !important;
    color: #8F5959 !important;
}
                   
.ui-dialog .ui-dialog-content {
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.9) !important;
}                   
                   
.to-top {
    position: fixed !important;
    top: 0px !important;
    bottom: 0px !important;
    left: 0px !important;
    padding: 0.3em !important;
    font: 20px PT Sans,Tahoma,Verdana,sans-serif !important;
    color: #E0EFF6 !important;
    height: 100% !important;
    opacity: 0.3 !important;
    display: none !important;
}                   
  /*Head Tabs*/
div.vectorTabs ul li {
    background-color: #302B2B !important;
    background-image: none !important;
}
    
div.vectorTabs li.selected {
    background-image: none !important;
}    

div.vectorTabs {
    background-image: none !important;
}    
    
div.vectorTabs span {
    background-image: none !important;
}    
    
div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
    color: #EAE0E0 !important;
}
    
div.vectorTabs li a {
    color: #D7B08F !important;
}    
    
div.flaggedrevs_short {
    border: 1px solid #634747 !important;
    background-color: rgba(255, 255, 255, 0) !important;    
}
    
div.flaggedrevs_short_details {
    border: 1px solid rgb(158, 119, 119) !important;
    background-color: rgb(36, 29, 29) !important;
}
   /*Images Section*/ 
.mw-mmv-info-box {
    border-width: 2px 2px 2px !important;
    border-color: #776060 #5D4545 #1E1515 !important;
    background-color: #1E1717 !important;
}
  
.mw-mmv-permission-box .mw-mmv-permission-text .mw-mmv-permission-text-fader {
    background-image: -moz-linear-gradient(center top , rgba(68, 44, 44, 0.01) 0%, rgba(77, 53, 53, 0.8) 100%) !important;
}
    
.mw-mmv-permission-box .mw-mmv-permission-text .mw-mmv-permission-text-fader a {
    background-color: #140C0C !important;
}    
    
.mw-mmv-post-image.mw-mmv-untruncated, .jq-fullscreened .mw-mmv-post-image {
    box-shadow: 0px -7px 10px rgba(28, 177, 246, 0.52) !important;
}    
   
.html5.source-html5 .de1, .html5.source-html5 .de2 {
    background: none repeat scroll 0% 0% #271B1B !important;
}
    
body.skin-monobook div.mw-geshi, body.skin-chick div.mw-geshi, body.skin-vector div.mw-geshi {
    border: 1px dashed #14D467 !important;
    color: #A28B8B !important;
    background-color: #381F1F !important;
}
    
.html5.source-html5 .kw2 {
    color: #DDC9C9 !important;
}
    
pre, .mw-code {
    color: #DBCBCB !important;
}
    
code {
    color: #000;
    background-color: #F98632 !important;
    border: 1px solid #F35429 !important;
}
    
html body #simple sortable{
    background:#333; border:1px #aaa solid !important;
}

div.NavFrame div.NavHead {
    background: none repeat scroll 0% 0% #362F2F;
}
    
    
    
    
    
    
    
    
    
    
    
    
    
    
  
}
