style.css:
@-moz-document domain("commeaucinema.com") {
    
    h1, #page_fiche #main .box .body, .boxpleine .body, .box .body {
       color: #ddd;
    }
    
    a,#page_fiche a, #page_fiche #main a, #page_fiche #main .box .body a, #page_calendrier .flecheg, #page_calendrier .fleched {
        color: #f90 !important;
    }
    
    body, #page_index, #fond_pub, body #fond_pub, body:not(#page_index) #fond_pub {
        background-color: black !important;
        color: #ddd !important;
    }
    
    .actuwidget .fbcontent, #critiques_presse tr:nth-child(1n), .body tr:nth-child(1n), .body li:nth-child(1n) {
        background-color: #222 !important;
        color: #ddd !important;
    }
    
    #globalcontainer, #playersidebar, .zoomdiv, .actuwidget.topactus .contentarea .boxitem ul li, .topactus .contentarea ul li, #side .box.topactus .body ul li, #critiques_presse tr:nth-child(2n)
    , .body tr:nth-child(2n), .body li:nth-child(2n)    {
        background-color: #333 !important;
        color: #ddd !important;
    }
    
    #main, .form_agenda, li:nth-child(1n) .thumb {
        background-color: #444 !important;
        color: #ddd !important;
    }
    
    li:nth-child(2n) .thumb, .fiche_shares, .flashbox .fbcontent {
        background-color: #555 !important;
        color: #ddd !important;
    }
}
