style.css:
@-moz-document domain("github.com") {
.container {
  width: 85% !important;
}

.tab-content, 
[class*="-with-sidebar"] {
  display: flex;
  width: auto;
}

.tab-content > [class*="-container"], 
[class*="-with-sidebar"] > [class*="-container"]{
  width: auto; 
  flex: 1;
  order: 0;
}

.tab-content > [class*="-sidebar"],
[class*="-with-sidebar"] > [class*="-sidebar"] {
  width: auto;
  margin-left: 20px;
  order: 1;
}
}
