style.css:
@-moz-document domain("ai2.appinventor.mit.edu") {
/* ==== mit ai2  -  material
		(re)design by daylo
		cc-by
		
		version 0.1.5-beta
		= first release
   ==== */

/* ====
		Commons
   ====
*/

body {
  background: #fff;
}

#page, #aiac .main-container, .container, .DialogBox-message > div {
  width: 100%;
  font-family: Roboto, 'Roboto', Helvetica, Arial, sans-serif !important;
}

/* == button design == */
div.ode-TopPanelButton, .ActionDropDown-Default, .ActionDropDown-Options, #menu-bar li {
  height: 40px;
  border-radius: 3px;
  margin-right: 1px;
  text-transform: uppercase;
}
div.ode-TopPanelButton:hover, .ActionDropDown-Default:hover .ActionDropDown-Options:hover, #menu-bar li:hover {
  background-color: #388E3C;
  color: #fff;
}
td.ode-ContextMenuItem-selected {
  background: rgba(0, 0, 0, 0.035);
  color: #4CAF50;
}

.ode-TextButton, .gwt-Button {
  background: #fff;
  color: #4CAF50;
  text-shadow: 0 0 0;
  text-transform: uppercase;
  border-color: transparent;
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.23);
  border-radius: 2px;
}
.ode-TextButton:hover, .gwt-Button:hover {
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.23);
}
#menu-bar li a {
  color: #fff;
  text-transform: uppercase;
}
#menu-bar li a:hover {
  color: #fff;
  text-transform: uppercase;
}
#themeplacehold_daylo, .ode-TextButton:hover, .gwt-Button:hover {
  background: #A5D6A7;
  color: #fff;
  text-shadow: 0 0 0;
  text-transform: uppercase;
  border-color: transparent;
}

/* == dropdown design == */
div.gwt-PopupPanel {
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.24);
  border: 0;
  border-radius: 2px;
  margin-right: 10px;
  padding: 3px;
}

/* == input boxes == */
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input, select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
  border: 0;
  box-shadow: 0 0 0;
  background: rgba(255,255,255,0.6);
}

/* ====
		All Pages
   ====
*/

/* == header == */
table.ode-TopPanel, #top_bar, #top_bar_background {
  background: #4CAF50;
  border: 0;
}
table.ode-TopPanel > tbody > tr > td[width="50px"], a[title="Home page"] {
  background-image: url(data:image/png;base64,);
  background-size: 40px;
  background-repeat: no-repeat;
  margin-top: 5px;
}
a[title="Home page"] {
  height: 40px;
  width: 40px;
  display: block;
}
a[title="Home page"] > img { display: none!important; }
div.ode-LogoText, .ode-TopPanelButton, .ActionDropDown-Default, .ActionDropDown-Options {
  color: #ffffff;
}
table.ode-TopPanelAccount > tbody > tr > td:nth-child(2) > div {
  font-size: 0;
  width: 24px;
  height: 24px;
  background-image: url(data:image/png;base64,);
  background-repeat: no-repeat;
  padding-right: 0;
}
table.ode-SimplePaletteItem > tbody > tr > td:nth-child(3) {
  background: url(data:image/png;base64,) no-repeat;
  width: 20px;
  height: 20px;
  background-size: 100%;
  z-index: 100000;
}
table.ode-SimplePaletteItem > tbody > tr > td:nth-child(3) > img {
  opacity: 0.01;
  height: 20px;
  width: 20px;
}
img.gwt-Image:not([src="http://ai2.appinventor.mit.edu/ode/clear.cache.gif"]), .ode-Logo, .ode-StatusPanel, .ode-LogoVersion, .ode-Box-header, .gwt-CheckBox, .ode-SimpleMockFormPhoneBar > tbody { display: none }

/* ====
		splash
   ====
*/
div.ode-DialogBox {
  border: 0;
  font-family: Roboto, "Roboto", Helvetica, sans-serif;
}
div.ode-DialogBox > div > table {
  max-height: 500px !important;
}
tr.dialogTop, .ode-DialogBox .Caption {
  background-color: #4CAF50!important;
  box-shadow: 0 0 0;
}

/* ====
		index (project choosing)
   ====
*/

/* == project opts == */
table.ya-Toolbar {
  background: #fff;
}
div.ya-ProjectName {
  background-color: #4CAF50;
  border-radius: 2px;
  padding-bottom: 5px;
}
/* == project table == */
table.ode-Box-header, .ode-Box-content, .ode-ProjectHeaderRow {
  border: 0;
  background: #ffffff;
}
div.ode-ProjectHeaderLabel, .ode-ProjectNameLabel, .gwt-Label {
  font-size: 16px;
}

/* ====
		app designer page
   ====
*/

/* == helper == */
div.ode-ComponentHelpPopup-TitleBar {
  padding: 0;
  background: #4CAF50;
}
div.ode-ComponentHelpPopup {
  border: 0;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.24);
}
#themeplacehold_daylo, .ode-ComponentHelpPopup-Link a {
  color: #4CAF50;
  text-decoration: none;
}
#themeplacehold_daylo, .ode-ComponentHelpPopup-Link a:hover {
  color: #388E3C;
  text-decoration: none;
}

/* == mockphone == */

/* = status bar = */

table.ode-SimpleMockFormPhoneBar {
background: url(data:image/png;base64,);
}
div.ode-SimpleMockFormTitleBar {
  background: #ffffff;
  box-shadow: 0px 1px 3px 0px rgba(20, 20, 20, 0.75);
  font-size: 130%;
}
div.ode-SimpleMockFormTitle {
  color: #1f1f1f;
  font-size: 95%;
}

/* = phone size etc = */
div.ode-SimpleMockForm, div.ode-SimpleMockForm-selected, .ode-SimpleMockContainer {
  border: 1px solid rgba(0,0,0,.25);
  background: #ffffff;
}
div.ode-SimpleMockForm > div:nth-child(3) > div > div, div.ode-SimpleMockContainer {
  width: 100%!important;
}

/* ====
		blocks page
   ====
*/
path {
  font-family: Roboto, 'Roboto', Helvetica, Arial, sans-serif;
}
/* == colour blocks == */

/* = control block = */
path[fill="#b18e35"], path[stroke="#c8b072"], path[fill="#6a5520"] {
  fill: #FDD835;
  stroke: #fdd31c;
  stroke-width: 0.5px;
}
/* = logic block = */
path[fill="#77ab41"], path[stroke="#a0c47a"], path[fill="#476727"] {
  fill: #43A047;
  stroke: #3b8e3f;
  stroke-width: 0.5px;
}
/* = maths block = */
/* maths, not math!! */
path[fill="#3f71b5"], path[stroke="#799ccb"], path[fill="#26446d"] {
  fill: #01579B;
  stroke: #014982;
  stroke-width: 0.5px;
}
/* = text block = */
path[fill="#b32d5e"], path[stroke="#ca6c8e"], path[fill="#6b1b38"] {
  fill: #F44336;
  stroke: #f32c1e;
  stroke-width: 0.5px;
}
/* = lists block = */
path[fill="#49a6d4"], path[stroke="#80c1e1"], path[fill="#2c647f"] {
  fill: #448AFF;
  stroke: #2b7aff;
  stroke-width: 0.5px;
}
/* = colours block = */
/* british spelling */
path[fill="#7d7d7d"], path[stroke="#a4a4a4"], path[fill="#4b4b4b"] {
  fill: #757575;
  stroke: #686868;
  stroke-width: 0.5px;
}
/* = variables block = */
path[fill="#d05f2d"], path[stroke="#de8f6c"], path[fill="#7d391b"] {
  fill: #E64A19;
  stroke: #cf4317;
  stroke-width: 0.5px;
}
/* = procedures block = */
path[fill="#7c5385"], path[stroke="#a387aa"], path[fill="#4a3250"] {
  fill: #673AB7;
  stroke: #5c34a4;
  stroke-width: 0.5px;
}
/* = attributes block = */
path[fill="#266643"], path[stroke="#67947b"], path[fill="#173d28"] {
  fill: #2E7D32;
  stroke: #276a2b;
  stroke-width: 0.5px;
}
/* = propgrab block = */
path[fill="#439970"], path[stroke="#7bb89b"], path[fill="#285c43"] {
  fill: #4CAF50;
  stroke: #449d48;
  stroke-width: 0.5px;
}
/* = maths op colouring = */
#themeplacehold-daylo, .blocklyNonEditableText>rect, .blocklyEditableText>rect {
  fill-opacity: 0.7;
}
/* = param colouring = */
#themeplacehold-daylo, .blocklyFieldParameter>rect {
  fill: rgba(255,255,255,0.7);
  stroke: rgba(255,255,255,1);
}
rect.blocklyIconShield {
  stroke-width: 0;
}
#themeplacehold-daylo, .blocklyFieldParameter:hover>rect {
  fill: rgba(255,255,255,0.9);
}
/* = comment colouring = */
rect[fill="#b18e35"] {
  fill: rgba(255,255,255,0.7);
  stroke: rgba(0,0,0,0.2);
  stroke-width: 1px;
}
textarea.blocklyCommentTextarea {
  background: rgba(255,255,255,0.5);
}
/* = scrolls = */
rect.blocklyScrollbarBackground {
  fill-opacity: 0.7;
}
}
