style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* General */
@-moz-document url-prefix("https://www.runtastic.com/en/") {
  #add_ad_space { display:none; }
}

/* Routes */
@-moz-document url-prefix("https://www.runtastic.com/en/routes/") {
  .route.show .main_info .other { display:none; }
}
@-moz-document url("https://www.runtastic.com/en/routes/new") {
  .ads_on_map { display: none; }
}

/* Dashboard */
@-moz-document url-prefix("https://www.runtastic.com/en/users/") {
  .line.live_box_headline, .live_box,
  .social_box { display:none; }
  
  #feed_tabs_js, #feed_js, #feed_load_more_js, #feed_no_entries_js,
  #statusform { display:none; }
}
