style.css:
/* New, modern and magical flat design for your new tab-page made by: Magical Magician with super rainbow awesome css powers */

@-moz-document url("about:newtab"){
   
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(http://fonts.gstatic.com/s/opensans/v10/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff2'), url(http://fonts.gstatic.com/s/opensans/v10/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
  }
  
  * {
    font-family: 'Open Sans', sans-serif !important;

  }
  
  div#newtab-scrollbox {
   background: #ecf0f1 !important;
  }
  
  input#newtab-search-text {
    background: #bdc3c7 !important;
    border: none !important;
    box-shadow: none !important;
  }
  
  input#newtab-search-submit {
    color: #ecf0f1 !important; 
    background: #2980b9 !important;
    border: none !important;
    box-shadow: none !important;
  }
  
  input#newtab-search-submit:hover {
    background: #3498db !important;
  }
  
  input#newtab-search-submit:active, input#newtab-search-submit:focus {
    background: #2A73A3 !important;
  }
  
  div.newtab-site {
    box-shadow: none !important;
    border: none !important;
  }
  
  div.newtab-cell {
   margin-right: -1px !important;
    margin-bottom: -13px !important;
  }
  
  span.newtab-title {
    display: none !important;
  }
  
}
