style.css:
@-moz-document url-prefix("http://www.haosou.com/s") {
*:not([class*="icon"]):not(i){font-family:"Microsoft Yahei"!important}
*{text-shadow: silver 0px 0px 1px !important}
body{background:#eee!important}
#main{float:none!important;width:90%!important;margin:0 auto!important;padding:80px 0 0 0!important}
#container{padding-left:0!important}
.result{margin:0 auto!important}
#side,.feedback,.searchGuide,#m-spread-left,#mohe-qihuwenda,#search,#g-hd-quser{display:none!important}
.g-mohe, .warning_custom{width:100%!important}
#page,.warning_main{margin:0 auto!important;width:90%!important}
.warning_custom{background:#fff;margin:0 0 20px 0!important}
.warning_custom >.warning_main{border:none!important;width:auto!important}
#rs{background:#fff!important;padding:8px 0!important;margin:20px auto 20px auto!important;width:90%}
#rs a,.res-title a,.title a{text-shadow: 0 0 20px #ccc}
.res-title a,.title a,.warning_custom >.warning_main>dt{font-size:18px!important}
.res-desc, .res-rich>div,.res-rich >em,.warning_custom >.warning_main>dd{font-size:16px!important}
.res-linkinfo{padding:10px 0 0 0!important}
.res-title{padding:0 0 10px 0!important}
.so-pdr{margin:0 0 20px 0!important}
#head{padding:20px!important;position:fixed!important;top:0;left:0;background:#fff!important;width:100%;z-index:999;margin-bottom:0}
.res-list{background:#fff!important;padding:20px!important;-webkit-border-radius:8px;-moz-border-radius:8px;border-radius:8px;-moz-box-shadow:0 1px 2px #909090;-webkit-box-shadow:0 1px 2px #909090;box-shadow:0 1px 2px #909090}
.res-desc,.mh-first-cont,.warning_custom >.warning_main>dd{line-height:1.6em!important}
.title a:hover,.res-title a:hover{padding:0 0 0 20px}
a {-moz-transition: all 0.3s ease-in;-webkit-transition: all 0.3s ease-in;-o-transition: all 0.3s ease-in;transition: all 0.3s ease-in}
#rs a:hover, .res-title a:hover,.title a:hover{color:#333!important}
}
