style.css:
@-moz-document domain("nyaa.eu"), domain("nyaa.se") {
#main{
width:100% !important;
}
 
.content{
width:100% !important;
border:0px !important;
}
  
#tabnav,
#main > div:nth-child(4),
#main > div:nth-child(1),
#main > div:nth-child(2),
#main > div.content > form,
#main > div.content > table.torrentcats,
iframe{display:none !important;}

#main > div.content > table.tlistsortorder{position: fixed; top:0px !important;}
  
#topbar {background-image:none !important;background-color: #dcdcdc !important;}
  
#searchcontainer{width:43%; position: fixed; top:1.5px; right:-55px  !important;}
}
