style.css:
@-moz-document domain("forum.idws.id") {

	* {
		box-shadow: none !important;
		text-shadow: none !important;
		transition: none !important;
		-webkit-transition: none !important;
		-moz-transition: none !important;
	}

	html,
	.button,
	.redactor_toolbar,
	.navTabs .navTab.selected .navLink,
	.navTabs .navTab.selected .tabLinks,
	.navTab.forums.Popup.PopupControl.PopupContainerControl.PopupOpen,
	.message .newIndicator,
	.messageUserBlock,
	.tabLinks,
	.breadcrumb,
	.attribution,
	.quoteContainer,
	.secondaryContent,
	.userBanner,
	.mast .subHeading,
	.prefix,
	.sectionFooter,
	.tabs li a,
	.tabs.noLinks li,
	.panel.Notice,
	.bbCodeBlock .type,
	.discussionList .sectionHeaders,
	.bbCodeBlock pre,
	.bbCodeBlock .code {
		background-image: none;
	}

	.messageUserBlock .extraUserInfo {
		opacity: 1;
		max-height: none;
	}

	.messageUserBlock .userBanner {
		margin-top: 5px;
	}

	.discussionList .sectionHeaders,
	.nodeList .categoryStrip {
		background: #f9d9b0;
	}

}


@-moz-document url-prefix("http://forum.idws.id/members/") {

	.mainProfileColumn {
		background: #f0f7fc;
		padding-bottom: 5px;
	}

	.mast .avatarScaler {
		text-align: center;
	}

	.primaryContent.messageSimple {
		padding: 10px 10px 0px;
	}
	
	#profilePosts {
		margin-right: 15px;
	}
}
