style.css:
@namespace url(http://www.w3.org/1999/xhtml);


/*\
 *
 * @name        Kaskus Minimus
 * @description Removes site features, leaving only the core forum features and enhances the interface.
 * @author      s4nji
 * @namespace   https://github.com/s4nji/kaskus-userstyles
 * @revision    1
 *
\*/


@-moz-document domain("kaskus.co.id") {

  /* Dummy Material for Kaskus Quick Reply Integration */
  body { content: 'kaskus-minimus'; }


  /* general */
   .thumbnail, .thread-prevnext, .abbr[data-original-title], abbr[title], .accessibility, #thread-tools, .app, .user-control-stick, #kk-playground, .notice, .cbc, .cb-x, .img_ad, .ads, .bnr-ads, .kaskus-ads, .kasad-h, #btn-whatsnew, .ads-lb-wrap, .sidebar, .related-thread, .quick-navigation, .tools-panel.share, #feedback, #div-gpt-ad-center, .kasad-piece, .smiley-tab {
    display: none !important;
  }
  
  /* thread view */
  .main-content { width: 100%; }
  .user-control-stick { width: 1170px; }
  
  body.fjb #breadcrumbs-wrap {
    background: none repeat scroll 0 0 #f5f5f5;
    border: 1px solid #d4d4d4;
    font-size: 12px;
    height: 29px;
    margin: 0 0 10px;
    overflow: hidden;
    padding: 0 10px;
    position: relative;
}
  
  .abgc {
    text-rendering: none;
}
  
  .postlist .entry-body{min-width:1048px!important}
  
  .entry-body{min-height:275px!important}
  
  /* ucp */
  .user-home-content table.post-entry tbody td:nth-child(1) { width: 5%; }
  .user-home-content table.post-entry tbody td:nth-child(2) { width: 65%; }
  .user-home-content table.post-entry tbody td:nth-child(3) { width: 10%; }
  .user-home-content table.post-entry tbody td:nth-child(4) { width: 10%; }
  .user-home-content table.post-entry tbody .author { float: left; width: auto; }
  .user-home-content table.post-entry tbody .act { float: right; margin-top: -16px; }
  
  .user-home-content table:not(.post-entry) tbody td:nth-child(1) { width: 3.3%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(2) { width: 45%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(3) { width: 6.7%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(4) { width: 35%; }
  
}
