style.css:
@-moz-document url-prefix("http://usepanda.com/app/") {
.column.column-news {
	max-width: 24%;
}

.column-content header {
	width: 100%;
}

.column.column-news li {
	width: 100%;
}

.column.column-news li .num,
.column.column-news li .num-rel {
	position: absolute;
}

.column.column-news li .num {
    top: 5px;
}

.column.column-news li .num-rel .num {
    top: 0;
}
    
.column.column-news li .right {
	padding-left: 30px;
	width: 100%;
}

}
