style.css:
@-moz-document regexp("https?://(www.)?youtube.com/(?!dashboard|my_videos|edit|analytics|features|audiolibrary/music|comments)(.*)") {
/* BASED ON AWESOME SCRIPT: Youtube - Lights Out! A Dark Youtube Theme From Draxx7*/


/* Common */
h3 { 
color: white !important;
}

h1 {
  color: white;
  font-size: 28px !important;
  margin: 20px 0 !important;
}

.info-popup {
  border: 1px solid #323232 !important;
  color: #ccc !important;
  background-color: #000 !important;
  box-shadow: 0 4px 16px #343434 !important;
  }

/*#249AD8*/
a {
  color: #fff !important;
}

a:hover {
  color: #ccc !important;
}


p {
  color: #C0C0C0 !important;
}

.ytp-volume-slider-handle:after {
    background: none !important;
}

.ytp-volume-slider-handle:before {
    background: #ffffff !important;
}
.yt-alert-warn .yt-alert-content, .yt-alert-warn .yt-alert-buttons .yt-uix-button-content {
  color: #C8C8C8 !important;
  text-shadow: none;
}

.html5-compatibility-table li {
  background: #1F1E1E;
  box-shadow: 0 1px 1px #2F2F2F !important;
}
  
.yt-alert-naked .yt-alert-content {
  color: #F2F2F2 !important;
}
  
