#autoplay-checkbox-label{
  background-color: #666 !important;
  border: solid 1px #444 !important;
}

#watch7-sidebar-modules > div:nth-child(1) > div > div.checkbox-on-off > span.autoplay-hovercard.yt-uix-hovercard > span {
display: none !important;
}
.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
  background-color: #666 !important;
  border: solid 1px #444 !important;
}

#watch7-sidebar .watch-sidebar-head {
  font-size: 11px !important;
}

#watch7-sidebar-modules > div:nth-child(1) > div > div.checkbox-on-off > label {
font-size: 10px !important;
}


#watch-like:before {
  background: no-repeat url(http://i.imgur.com/HZQMHOi.png) -557px -108px !important;

}
#watch-dislike:before {
  background: no-repeat url(http://i.imgur.com/HZQMHOi.png) -39px -154px !important;
}
  
