style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.fr") {

/*Animation des potatoes-volants du 1er avril*/
  
html {
  background : #2894FD url("http://i61.tinypic.com/33z46di.png") center top 0 fixed repeat ;
  background-size: 100%
} 
  
body {
  background-color : transparent !important ;
  background-image : url("http://i61.tinypic.com/2zfop6f.png") ,
    url("http://i61.tinypic.com/311rzig.png") ,
    url("http://i62.tinypic.com/10dyvlf.png")  !important ;
  background-attachment : fixed ;
  background-repeat : no-repeat ;
  -webkit-animation : nagezpoissons 25s linear infinite ;
  animation : nagezpoissons 25s linear infinite ;    
}

@-webkit-keyframes nagezpoissons {
  0% {background-position : -120px 0, calc(100% + 120px) 0, calc(100% + 120px) calc(100% - 150px)  ;}
  10% {background-position : 10% 0, calc(100% + 120px) 0, calc(100% + 120px) calc(100% - 150px) ;}
  20% {background-position : 30% 10%, 90% 20%, calc(100% + 120px) calc(100% - 150px) ;}
  100% {background-position : 120% 20%, 0 calc(100% + 120px), -10% 35% ;}
}    
  
@keyframes nagezpoissons {
  0% {background-position : -120px 0, calc(100% + 120px) 0, calc(100% + 120px) calc(100% - 150px)  ;}
  10% {background-position : 10% 0, calc(100% + 120px) 0, calc(100% + 120px) calc(100% - 150px) ;}
  20% {background-position : 30% 10%, 90% 20%, calc(100% + 120px) calc(100% - 150px) ;}
  100% {background-position : 120% 20%, 0 calc(100% + 120px), -10% 35% ;}
}

