/*Кнопка "Больше"*/

div#id-prev-comm-link-w { margin: 2px 0 !important; text-align: center !important; overflow: visible !important; width: 543px !important; padding: 0 0 0 40px !important; -moz-box-sizing: border-box !important; box-sizing: border-box !important; }
