#topPanelPopup_d .d_comment_w__avatar .d_comment_left_w, .d_comment_left_w__compressed { width: 36px; margin-top: 2px; }

#topPanelPopup_d .d_comment_right_w { min-width: 0; max-width: 416px; padding: 4px 8px; overflow: visible; display: inline-block; border: 1px solid #fff; border-radius: 3px; background: #fff; text-align: left; -webkit-transition: border-color .6s; transition: border-color .6s; width: auto; float: none; padding-right: 32px; box-shadow: 0 1px 1px rgba(0, 0, 0, .07); }

