#topPanelPopup_d .d_comment_r { position: absolute; top: 10px; left: -48px; margin-left: 1px; padding: 8px; display: block; float: none; width: auto; min-width: 0; border-radius: 9px; line-height: 10px; white-space: nowrap; padding-top: 2px; margin-top: -20px; margin-top: 0px !important; overflow: visible !important; }

