#topPanelPopup_d .d_comment_act_spam.d_comment_act_del { margin-top: -1px; }

#topPanelPopup_d .d_comment_time { margin: 0; float: none; display: inline-block !important; vertical-align: bottom; color: #999; line-height: 1; right: 5px; top: -14px; position: relative; }

#topPanelPopup_d .d_comment_reply_w { position: absolute; top: 0px; left: 100%; margin: 0 -8px 0px -12px; padding-bottom: 0px; overflow: hidden; min-height: 35px; }

