.islands #hook_Block_MainFeedsWrapper .portlet, .islands #hook_Block_AltGroupMainFeedsNewRB .portlet, .islands #hook_Block_AltGroupSearchWrapper .portlet {
    background: transparent;
    padding: 0 !important;
}


/* 2. Карточка ленты */

.feed {
    font-size: 16px;
    font-weight: 400;
    min-height: 75px;
    overflow: hidden;
    width: 100%;
    z-index: 1;
    position: relative;
    background: #fff;
    border-radius: 2px;
    box-sizing: border-box;
    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, .14), 0 1px 1px -2px rgba(0, 0, 0, .2), 0 1px 1px 0 rgba(0, 0, 0, .12);
    margin: 10px 0 !important;
    padding: 0 !important;
}


/* 2.1 Коррекция внутренней карточки */

.feed.__shared {
    box-shadow: none;
    margin: 0;
}


/* 2.2 Основное содержимое карточки */

.feed_cnt {
    display: flex;
    flex-direction: column;
    margin: 0 !important;
    left: 0;
    width: 100%;
}


/* 2.2.1 Блок с заголовком */

.feed_h, .feed-i_action {
    box-sizing: border-box;
    font-size: 1rem;
    line-height: 18px;
    overflow: hidden;
    padding: 16px;
    padding-left: 72px;
    padding-right: 85px;
    margin-left: 0px;
    margin-bottom: 0px !important;
    width: calc(100%);
    min-height: 72px;
}


/* 2.2.1.1 Вторичный заголовок */

.__no-ava .feed_h:nth-child(2) {
    min-height: 55px;
}


/* 2.2.1.2 Заголовок без авы */

.__no-ava > .feed_h {
    margin-left: 0px;
    width: 100%;
    font-size: 0.9rem;
}


/* 2.2.2 Контент */

.feed_b {
    display: block;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-justify-content: stretch;
    -ms-flex-pack: stretch;
    justify-content: stretch;
    line-height: normal;
    width: 100%;
    box-sizing: border-box;
    flex-direction: column;
}

.media-block {
    box-sizing: border-box;
}


/* 2.2.3 Блок Фото */

.media-block.media-photos {
    width: 100%;
}


/* 2.2.4 Блок с текстом */

.media-block.media-text {
    color: rgba(0, 0, 0, 0.87);
    font-size: 14px;
    line-height: 1.4;
    overflow: hidden;
    padding: 16px;
    padding-bottom: 24px;
    width: 95%;
}

.media-block.media-text .media-text_cnt {
    max-height: 100%;
}


/* 2.2.4.1 Выделение статуса */

.media-status.__accent .media-status_cnt {
    padding: 0 !important;
    background: transparent !important;
    max-width: 100% !important;
}

.media-status {
    margin: 0px !important;
}


/* Сокрытие стрелочки */

.media-status.__arw-t:before {
    display: none !important;
}


/* 2.2.5 Блок с кнопками*/

.feed_f {
    margin: 0 !important;
    padding: 8px;
    min-height: 52px;
    box-sizing: border-box;
}


/* Позиционирование кнопок */

.widget-list {
    float: right;
}

.feed-i_links_i {
    margin: 0 !important
}

.widget-list_i {
    margin: 0 0 0 8px;
}


/* 2.2.6 Читать далее */

.media_more {
    margin: 0 !important;
    box-sizing: border-box;
    height: 20px !important;
    transition: all 0.4s ease;
}
.media_more_a {
    text-align: center;
}
.media_more:hover {
    height: 30px !important;
}

.media_more_ic::after {
    content: "Читать далее";
    opacity: 0;
    display: block;
    position: relative;
    top: -12px;
    transition: opacity 0.4s ease 0.2s;
}

.media_more:hover .media_more_ic::after {
    opacity: 1;
}

.media_more:hover .media_more_ic {
    opacity: 0
}

.media_more_ic {
    transition: opacity 0.5s ease
}

/* 2.2.7 Треки */
.track {
    position: relative;
    height: 40px;
    padding: 10px;
    transition-duration: .28s;
    transition-timing-function: cubic-bezier(.4, 0, .2, 1);
    transition-property: background-color;
    box-sizing: border-box;
}
/* 2.2.8 Теги */
.tag-box__list {
    padding: 16px;
    height: 20px;
}

.tag-box__list .tico {
    height: 20px;
    width: 30px;
    padding: 0 !important;
    vertical-align: middle;
}
/* 2.3 Аватарки*/


/* 2.3.1 Позиционирование в контейнере */

.feed_ava {
    position: absolute;
    z-index: 2;
    margin: 16px;
}


/* 2.3.2 Округлые края */

.feed.__shared .feed_ava_img, .feed.__shared .feed_ava .stub-img, .feed.__shared .feed_label, .feed_ava .feed_ava_img, .groups_post_author_avatar.ucard img, .groups_post_content__w-avatar {
    margin-left: 0 !important;
    border-radius: 50%;
}


/* 2.3.3 Коррекция позиции авы в группе */

.groups_post_author_avatar.ucard {
    top: 16px;
    left: 16px;
}


/* 2.4 Время и действия */

.feed_ac {
    z-index: 5;
    top: 18px !important;
    right: 18px;
}


/* 2.4.1 Дата */

.feed_date {
    opacity: 1 !important;
    visibility: visible !important;
    font-size: 0.8rem !important;
}
